INSERT INTO `tb_article` VALUES ('1501381359235', '美文', '5', 'teacher', '2017-07-30 00:00:00', '<p style=\"color:#4F5D41;font-family:Simsun;font-size:14px;background-color:#FFFFFF;\">\n	我们爱过\n</p>\n<p style=\"color:#4F5D41;font-family:Simsun;font-size:14px;background-color:#FFFFFF;\">\n	得到过自己想要的\n</p>\n<p style=\"color:#4F5D41;font-family:Simsun;font-size:14px;background-color:#FFFFFF;\">\n	想象着会有很好的未来\n</p>\n<p style=\"color:#4F5D41;font-family:Simsun;font-size:14px;background-color:#FFFFFF;\">\n	有你有我\n</p>\n<p style=\"color:#4F5D41;font-family:Simsun;font-size:14px;background-color:#FFFFFF;\">\n	可是多年以后\n</p>\n<p style=\"color:#4F5D41;font-family:Simsun;font-size:14px;background-color:#FFFFFF;\">\n	蓦然回首\n</p>\n<p style=\"color:#4F5D41;font-family:Simsun;font-size:14px;background-color:#FFFFFF;\">\n	发现自己\n</p>\n<p style=\"color:#4F5D41;font-family:Simsun;font-size:14px;background-color:#FFFFFF;\">\n	一无所有\n</p>\n<p style=\"color:#4F5D41;font-family:Simsun;font-size:14px;background-color:#FFFFFF;\">\n	真的\n</p>\n<p style=\"color:#4F5D41;font-family:Simsun;font-size:14px;background-color:#FFFFFF;\">\n	真的一无所有\n</p>\n<p style=\"color:#4F5D41;font-family:Simsun;font-size:14px;background-color:#FFFFFF;\">\n	可那时\n</p>\n<p style=\"color:#4F5D41;font-family:Simsun;font-size:14px;background-color:#FFFFFF;\">\n	我们没有了继续追逐的勇气\n</p>\n<p style=\"color:#4F5D41;font-family:Simsun;font-size:14px;background-color:#FFFFFF;\">\n	没有了再爱一次的勇气\n</p>\n<p style=\"color:#4F5D41;font-family:Simsun;font-size:14px;background-color:#FFFFFF;\">\n	我们的存在\n</p>\n<p style=\"color:#4F5D41;font-family:Simsun;font-size:14px;background-color:#FFFFFF;\">\n	或许只是一次巧合\n</p>\n<p style=\"color:#4F5D41;font-family:Simsun;font-size:14px;background-color:#FFFFFF;\">\n	可是为什么\n</p>\n<p style=\"color:#4F5D41;font-family:Simsun;font-size:14px;background-color:#FFFFFF;\">\n	我没有渴望被爱\n</p>\n<p style=\"color:#4F5D41;font-family:Simsun;font-size:14px;background-color:#FFFFFF;\">\n	如果我们像天上的流星\n</p>\n<p style=\"color:#4F5D41;font-family:Simsun;font-size:14px;background-color:#FFFFFF;\">\n	又为什么\n</p>\n<p style=\"color:#4F5D41;font-family:Simsun;font-size:14px;background-color:#FFFFFF;\">\n	会真实存在\n</p>\n<p style=\"color:#4F5D41;font-family:Simsun;font-size:14px;background-color:#FFFFFF;\">\n	感谢我们曾经遇见\n</p>\n<p style=\"color:#4F5D41;font-family:Simsun;font-size:14px;background-color:#FFFFFF;\">\n	感谢我们曾经寒暄\n</p>\n<p style=\"color:#4F5D41;font-family:Simsun;font-size:14px;background-color:#FFFFFF;\">\n	谢谢你们\n</p>\n<p style=\"color:#4F5D41;font-family:Simsun;font-size:14px;background-color:#FFFFFF;\">\n	带我走过寒暑冬夏\n</p>\n<p style=\"color:#4F5D41;font-family:Simsun;font-size:14px;background-color:#FFFFFF;\">\n	&nbsp;\n</p>\n<p style=\"color:#4F5D41;font-family:Simsun;font-size:14px;background-color:#FFFFFF;\">\n	青春不会有散场\n</p>', '0', null, null, null, null, null);
