ull, null);
INSERT INTO `tb_praxis` VALUES ('1491294160290', '&nbsp;\r\n<p style=\"text-align:center;\" class=\"MsoNormal\" align=\"center\">\r\n	<b><span style=\"line-height:150%;font-family:宋体;font-size:22pt;\">笔试题</span></b><b><span style=\"font-family:宋体;\"></span></b>\r\n</p>\r\n<div>\r\n	<p class=\"MsoNormal\">\r\n		<b><span style=\"line-height:150%;font-family:宋体;\">姓名：</span></b><b><span style=\"line-height:150%;font-family:宋体;\"><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span></b><b><span style=\"line-height:150%;font-family:宋体;\">学校：</span></b><b><span style=\"line-height:150%;font-family:宋体;\"><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span></b><b><span style=\"line-height:150%;font-family:宋体;\">专业：</span></b><b><span style=\"line-height:150%;font-family:宋体;\"></span></b>\r\n	</p>\r\n</div>\r\n<p class=\"MsoNormal\">\r\n	<b><span style=\"font-family:宋体;color:black;font-size:10.5pt;\"></span></b>\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<b><span style=\"font-family:宋体;color:black;font-size:10.5pt;\">一、选择题</span></b><b><span style=\"font-family:宋体;color:black;font-size:10.5pt;\">(</span></b><b><span style=\"font-family:宋体;color:black;font-size:10.5pt;\">每题</span></b><b><span style=\"font-family:宋体;color:black;font-size:10.5pt;\">4</span></b><b><span style=\"font-family:宋体;color:black;font-size:10.5pt;\">分</span></b><b><span style=\"font-family:宋体;color:black;font-size:10.5pt;\">/</span></b><b><span style=\"font-family:宋体;color:black;font-size:10.5pt;\">共</span></b><b><span style=\"font-family:宋体;color:black;font-size:10.5pt;\">40</span></b><b><span style=\"font-family:宋体;color:black;font-size:10.5pt;\">分</span></b><b><span style=\"font-family:宋体;color:black;font-size:10.5pt;\">)</span></b>\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<b><span style=\"font-family:\'ˎ̥\',\'serif\';color:black;font-size:10.5pt;\">1</span></b><b><span style=\"font-family:宋体;color:black;font-size:10.5pt;\">、</span></b><span style=\"font-family:宋体;font-size:10.5pt;\">请问，为了使一个名为</span><span style=\"font-family:宋体;font-size:10.5pt;\">MyClass</span><span style=\"font-family:宋体;font-size:10.5pt;\">的</span><span style=\"font-family:宋体;font-size:10.5pt;\">public</span><span style=\"font-family:宋体;font-size:10.5pt;\">类成功编译，需满足以下哪些条件？</span><span style=\"font-family:宋体;font-size:10.5pt;\"></span>\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-family:宋体;font-size:10.5pt;\"></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormalIndent\">\r\n	<span style=\"font-family:宋体;\">请选择一个正确答案：</span><span style=\"font-family:宋体;\">(<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span>)</span><span style=\"font-family:宋体;\"></span>\r\n</p>\r\n<p style=\"text-indent:0mm;margin-left:21pt;\" class=\"MsoNormalIndent\">\r\n	<span style=\"font-family:宋体;\">(A) MyClass</span><span style=\"font-family:宋体;\">类中必须定义一个正确的</span><span style=\"font-family:宋体;\">main()</span><span style=\"font-family:宋体;\">方法。</span><span style=\"font-family:宋体;\"></span>\r\n</p>\r\n<p style=\"text-indent:0mm;margin-left:21pt;\" class=\"MsoNormalIndent\">\r\n	<span style=\"font-family:宋体;\">(B) MyClass</span><span style=\"font-family:宋体;\">类必须定义在</span><span style=\"font-family:宋体;\">MyClass.java</span><span style=\"font-family:宋体;\">源文件中。</span><span style=\"font-family:宋体;\"></span>\r\n</p>\r\n<p style=\"text-indent:0mm;margin-left:21pt;\" class=\"MsoNormalIndent\">\r\n	<span style=\"font-family:宋体;\">(C) MyClass</span><span style=\"font-family:宋体;\">类必须定义在</span><span style=\"font-family:宋体;\">MyClass</span><span style=\"font-family:宋体;\">包中。</span><span style=\"font-family:宋体;\"></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:宋体;font-size:10.5pt;\">(D) MyClass</span><span style=\"font-family:宋体;font-size:10.5pt;\">类必须被导入。</span><span style=\"font-family:宋体;font-size:10.5pt;\"></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:宋体;font-size:10.5pt;\"></span>\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<b><span style=\"font-family:宋体;font-size:10.5pt;\">2</span></b><b><span style=\"font-family:宋体;font-size:10.5pt;\">、</span></b><span style=\"font-family:宋体;font-size:10.5pt;\">以下叙述中正确的是：</span><span style=\"font-family:宋体;font-size:10.5pt;\">(<span><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span>)</span>\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-family:宋体;font-size:10.5pt;\"></span>\r\n</p>\r\n<p style=\"text-indent:20.25pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:宋体;font-size:10.5pt;\">(A)</span><span style=\"font-family:宋体;font-size:10.5pt;\">C</span><span style=\"font-family:宋体;font-size:10.5pt;\">语言比其他语言高级</span><span style=\"font-family:宋体;font-size:10.5pt;\">;</span>\r\n</p>\r\n<p style=\"text-indent:20.25pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:宋体;font-size:10.5pt;\">(B)</span><span style=\"font-family:宋体;font-size:10.5pt;\">C</span><span style=\"font-family:宋体;font-size:10.5pt;\">语言可以不用编译就能被计算机识别执行</span><span style=\"font-family:宋体;font-size:10.5pt;\">;</span>\r\n</p>\r\n<p style=\"text-indent:20.25pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:宋体;font-size:10.5pt;\">(C)</span><span style=\"font-family:宋体;font-size:10.5pt;\">C</span><span style=\"font-family:宋体;font-size:10.5pt;\">语言以接近英语国家的自然语言和数学语言作为语言的表达形式</span><span style=\"font-family:宋体;font-size:10.5pt;\">; </span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:宋体;font-size:10.5pt;\">(D)C</span><span style=\"font-family:宋体;font-size:10.5pt;\">语言出现的最晚，具有其他语言的一切优点</span><span style=\"font-family:宋体;font-size:10.5pt;\"></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormalIndent\">\r\n	<span style=\"font-family:宋体;\"></span>\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<b><span style=\"font-family:宋体;font-size:10.5pt;\">3</span></b><b><span style=\"font-family:宋体;font-size:10.5pt;\">、</span></b><span style=\"font-family:宋体;font-size:10.5pt;\">在</span><span style=\"font-family:宋体;font-size:10.5pt;\">C</span><span style=\"font-family:宋体;font-size:10.5pt;\">语言中将两个字符串连接起来组成一个字符串时，选用</span><span style=\"font-family:宋体;font-size:10.5pt;\">(<span><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span>)</span><span style=\"font-family:宋体;font-size:10.5pt;\">函数。</span><span style=\"font-family:宋体;font-size:10.5pt;\"></span>\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-family:宋体;font-size:10.5pt;\"></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormalIndent\">\r\n	<span style=\"font-family:宋体;\">(A) strlen( );</span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormalIndent\">\r\n	<span style=\"font-family:宋体;\">(B) strcpy( );</span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormalIndent\">\r\n	<span style=\"font-family:宋体;\">(C) strcat( );</span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormalIndent\">\r\n	<span style=\"font-family:宋体;\">(D) strcmp( );</span>\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:10.5pt;\"></span>\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<b><span style=\"font-family:宋体;font-size:10.5pt;\">4</span></b><b><span style=\"font-family:宋体;font-size:10.5pt;\">、</span></b><span style=\"font-family:宋体;font-size:10.5pt;\">给出以下</span><span style=\"font-family:宋体;font-size:10.5pt;\">Java</span><span style=\"font-family:宋体;font-size:10.5pt;\">代码，请问该程序的运行结果是什么？</span><span style=\"font-family:宋体;font-size:10.5pt;\"></span>\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-family:宋体;font-size:10.5pt;\"></span>\r\n</p>\r\n<div align=\"center\">\r\n	<table style=\"width:327pt;background:#e6e6e6;\" class=\"MsoNormalTable ke-zeroborder\" border=\"0\" cellpadding=\"0\" width=\"436\">\r\n		<tbody>\r\n			<tr>\r\n				<td valign=\"top\" width=\"432\">\r\n					<div align=\"center\">\r\n						<table style=\"width:327pt;background:#e6e6e6;\" class=\"MsoNormalTable ke-zeroborder\" border=\"0\" cellpadding=\"0\" width=\"436\">\r\n							<tbody>\r\n								<tr>\r\n									<td width=\"432\">\r\n										<p class=\"MsoNormal\">\r\n											<span style=\"font-family:宋体;font-size:10.5pt;\">class Example {</span>\r\n										</p>\r\n										<p style=\"text-indent:15.75pt;\" class=\"MsoNormal\">\r\n											<span style=\"font-family:宋体;font-size:10.5pt;\">public static void main(String args[]){</span>\r\n										</p>\r\n										<p style=\"text-indent:33.8pt;\" class=\"MsoNormal\">\r\n											<span style=\"font-family:宋体;font-size:10.5pt;\">boolean b = true;</span>\r\n										</p>\r\n										<p style=\"text-indent:33.8pt;\" class=\"MsoNormal\">\r\n											<span style=\"font-family:宋体;font-size:10.5pt;\">System.out.println(b);</span>\r\n										</p>\r\n										<p style=\"text-indent:15.75pt;\" class=\"MsoNormal\">\r\n											<span style=\"font-family:宋体;font-size:10.5pt;\">}</span>\r\n										</p>\r\n										<p class=\"MsoNormal\">\r\n											<span style=\"font-family:宋体;font-size:10.5pt;\">}</span>\r\n										</p>\r\n									</td>\r\n								</tr>\r\n							</tbody>\r\n						</table>\r\n					</div>\r\n					<p class=\"MsoNormal\">\r\n						<span style=\"font-size:10.5pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n		</tbody>\r\n	</table>\r\n</div>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormalIndent\">\r\n	<span style=\"font-family:宋体;\"></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormalIndent\">\r\n	<span style=\"font-family:宋体;\">请选择一个正确答案：</span><span style=\"font-family:宋体;\">(<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span>)</span>\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span></span>\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormalIndent\">\r\n	<span style=\"font-family:宋体;\">(A) </span><span style=\"font-family:宋体;\">打印输出：</span><span style=\"font-family:宋体;\">true</span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormalIndent\">\r\n	<span style=\"font-family:宋体;\">(B)&nbsp;</span><span style=\"font-family:宋体;\">打印输出：</span><span style=\"font-family:宋体;\">1</span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormalIndent\">\r\n	<span style=\"font-family:宋体;\">(C)&nbsp;</span><span style=\"font-family:宋体;\">编译错误，因为</span><span style=\"font-family:宋体;\">System.out.println()</span><span style=\"font-family:宋体;\">方法的参数只能为整数。</span><span style=\"font-family:宋体;\"></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormalIndent\">\r\n	<span style=\"font-family:宋体;\">(D)&nbsp;</span><span style=\"font-family:宋体;\">无内容输出。</span><span style=\"font-family:宋体;\"></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:宋体;font-size:10.5pt;\">(E)&nbsp;</span><span style=\"font-family:宋体;font-size:10.5pt;\">编译错误，如果把“</span><span style=\"font-family:宋体;font-size:10.5pt;\">true</span><span style=\"font-family:宋体;font-size:10.5pt;\">”改变为“</span><span style=\"font-family:宋体;font-size:10.5pt;\">TRUE</span><span style=\"font-family:宋体;font-size:10.5pt;\">”，则可使代码编译通过。</span><span style=\"font-family:宋体;font-size:10.5pt;\"></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:宋体;font-size:10.5pt;\"></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:宋体;font-size:10.5pt;\"></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:宋体;font-size:10.5pt;\"></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:宋体;font-size:10.5pt;\"></span>\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<b><span style=\"font-family:宋体;font-size:10.5pt;\">5</span></b><b><span style=\"font-family:宋体;font-size:10.5pt;\">、</span></b><span style=\"font-family:宋体;font-size:10.5pt;\">C</span><span style=\"font-family:宋体;font-size:10.5pt;\">语言中用于结构化程序设计的三种基本结构是</span><span style=\"font-family:宋体;font-size:10.5pt;\">(<span><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span>)</span><span style=\"font-family:宋体;font-size:10.5pt;\"></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormalIndent\">\r\n	<span style=\"font-family:宋体;\"></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormalIndent\">\r\n	<span style=\"font-family:宋体;\">(A) </span><span style=\"font-family:宋体;\">顺序结构、选择结构、循环结构</span><span style=\"font-family:宋体;\">;</span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormalIndent\">\r\n	<span style=\"font-family:宋体;\">(B)</span><span> </span><span style=\"font-family:宋体;\">if</span><span style=\"font-family:宋体;\">、</span><span style=\"font-family:宋体;\">switch</span><span style=\"font-family:宋体;\">、</span><span style=\"font-family:宋体;\">break;</span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormalIndent\">\r\n	<span style=\"font-family:宋体;\">(C)</span><span> </span><span style=\"font-family:宋体;\">for</span><span style=\"font-family:宋体;\">、</span><span style=\"font-family:宋体;\">while</span><span style=\"font-family:宋体;\">、</span><span style=\"font-family:宋体;\">do-while;</span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormalIndent\">\r\n	<span style=\"font-family:宋体;\">(D)</span><span> </span><span style=\"font-family:宋体;\">if</span><span style=\"font-family:宋体;\">、</span><span style=\"font-family:宋体;\">for</span><span style=\"font-family:宋体;\">、</span><span style=\"font-family:宋体;\">continue</span>\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-family:宋体;font-size:10.5pt;\"></span>\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<b><span style=\"font-family:宋体;font-size:10.5pt;\">6</span></b><b><span style=\"font-family:宋体;font-size:10.5pt;\">、</span></b><span style=\"font-family:宋体;font-size:10.5pt;\">有以下程序</span><span style=\"font-family:宋体;font-size:10.5pt;\">:</span>\r\n</p>\r\n<div align=\"center\">\r\n	<table style=\"width:327pt;background:#e6e6e6;\" class=\"MsoNormalTable ke-zeroborder\" border=\"0\" cellpadding=\"0\" width=\"436\">\r\n		<tbody>\r\n			<tr>\r\n				<td valign=\"top\" width=\"432\">\r\n					<div align=\"center\">\r\n						<table style=\"width:327pt;background:#e6e6e6;\" class=\"MsoNormalTable ke-zeroborder\" border=\"0\" cellpadding=\"0\" width=\"436\">\r\n							<tbody>\r\n								<tr>\r\n									<td width=\"432\">\r\n										<p class=\"MsoNormal\">\r\n											<span style=\"font-family:宋体;font-size:10.5pt;\">main()</span>\r\n										</p>\r\n										<p style=\"text-indent:26.25pt;\" class=\"MsoNormal\">\r\n											<span style=\"font-family:宋体;font-size:10.5pt;\">{ </span>\r\n										</p>\r\n										<p style=\"text-indent:42pt;\" class=\"MsoNormal\">\r\n											<span style=\"font-family:宋体;font-size:10.5pt;\">int i;</span>\r\n										</p>\r\n										<p style=\"text-indent:42pt;\" class=\"MsoNormal\">\r\n											<span style=\"font-family:宋体;font-size:10.5pt;\">for(i=0;i&lt;3;i++)</span>\r\n										</p>\r\n										<p style=\"text-indent:57.75pt;\" class=\"MsoNormal\">\r\n											<span style=\"font-family:宋体;font-size:10.5pt;\">switch(i)</span>\r\n										</p>\r\n										<p style=\"text-indent:63pt;\" class=\"MsoNormal\">\r\n											<span style=\"font-family:宋体;font-size:10.5pt;\">{ case 1: printf(\"%d\",i);</span>\r\n										</p>\r\n										<p style=\"text-indent:73.5pt;\" class=\"MsoNormal\">\r\n											<span style=\"font-family:宋体;font-size:10.5pt;\">case 2: printf(\"%d\",i);</span>\r\n										</p>\r\n										<p style=\"text-indent:73.5pt;\" class=\"MsoNormal\">\r\n											<span style=\"font-family:宋体;font-size:10.5pt;\">default: printf(\"%d\",i);</span>\r\n										</p>\r\n										<p style=\"text-indent:63pt;\" class=\"MsoNormal\">\r\n											<span style=\"font-family:宋体;font-size:10.5pt;\">}</span>\r\n										</p>\r\n										<p style=\"text-indent:21pt;\" class=\"MsoNormal\">\r\n											<span style=\"font-family:宋体;font-size:10.5pt;\">}</span>\r\n										</p>\r\n									</td>\r\n								</tr>\r\n							</tbody>\r\n						</table>\r\n					</div>\r\n					<p class=\"MsoNormal\">\r\n						<span style=\"font-size:10.5pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n		</tbody>\r\n	</table>\r\n</div>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-family:宋体;font-size:10.5pt;\"><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span><span style=\"font-family:宋体;font-size:10.5pt;\">执行后输出结果是</span><span style=\"font-family:宋体;font-size:10.5pt;\">:</span><span style=\"font-family:宋体;font-size:10.5pt;\"> (<span><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span>)</span><span style=\"font-family:宋体;font-size:10.5pt;\"></span>\r\n</p>\r\n<p style=\"margin-left:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:宋体;font-size:10.5pt;\">(A) 011122;</span>\r\n</p>\r\n<p style=\"margin-left:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:宋体;font-size:10.5pt;\">(B) 012 ;</span>\r\n</p>\r\n<p style=\"margin-left:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:宋体;font-size:10.5pt;\">(C) 012020 ;</span>\r\n</p>\r\n<p style=\"margin-left:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:宋体;font-size:10.5pt;\">(D) 120</span><span style=\"font-family:宋体;font-size:10.5pt;\"></span>\r\n</p>\r\n<p style=\"text-indent:0mm;\" class=\"MsoNormalIndent\">\r\n	<b><span style=\"font-family:宋体;\">7</span></b><b><span style=\"font-family:宋体;\">、</span></b><span style=\"font-family:宋体;\">在</span><span> Java </span><span style=\"font-family:宋体;\">中，由</span><span>Java</span><span style=\"font-family:宋体;\">编译器自动导入，而无需在程序中用</span><span>import</span><span style=\"font-family:宋体;\">导入的包是<span style=\"background:white;color:black;\">：</span></span><span style=\"font-family:宋体;\">(<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span>)</span><span> </span>\r\n</p>\r\n<p style=\"text-indent:0mm;\" class=\"MsoNormalIndent\">\r\n	<span></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormalIndent\">\r\n	<span style=\"font-family:宋体;\">(A)</span><span> java.applet <span>&nbsp;</span></span>\r\n</p>\r\n<p style=\"text-indent:20.05pt;\" class=\"MsoNormalIndent\">\r\n	<span style=\"font-family:宋体;\">(B) </span><span>java.awt </span><span style=\"font-family:宋体;\"></span>\r\n</p>\r\n<p style=\"text-indent:20.25pt;\" class=\"MsoNormalIndent\">\r\n	<span style=\"font-family:宋体;\">(C) </span><span>java.util </span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormalIndent\">\r\n	<span style=\"font-family:宋体;\">(D)</span><span> java.lang </span>\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:10.5pt;\"></span>\r\n</p>\r\n<p style=\"text-indent:0mm;\" class=\"MsoNormalIndent\">\r\n	<b><span style=\"font-family:宋体;\">8</span></b><b><span style=\"font-family:宋体;\">、</span></b><span style=\"font-family:宋体;\">在</span><span style=\"font-family:宋体;\">Oracle</span><span style=\"font-family:宋体;\">数据库中</span><span style=\"font-family:宋体;\">创建表时，一个</span><span style=\"font-family:宋体;\">VARCHAR2</span><span style=\"font-family:宋体;\">的列没有指定长度，它的缺省长度是</span><span style=\"font-family:宋体;\">(<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span>)</span><span style=\"font-family:宋体;\">？</span><span style=\"font-family:宋体;\"></span>\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-family:宋体;font-size:10.5pt;\"></span>\r\n</p>\r\n<p style=\"text-indent:15.75pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:宋体;font-size:10.5pt;\">（</span><span style=\"font-family:宋体;font-size:10.5pt;\">A</span><span style=\"font-family:宋体;font-size:10.5pt;\">）</span><span style=\"font-size:10.5pt;\">1</span><span style=\"font-family:宋体;font-size:10.5pt;\"></span>\r\n</p>\r\n<p style=\"text-indent:15.75pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:宋体;font-size:10.5pt;\">（</span><span style=\"font-family:宋体;font-size:10.5pt;\">B</span><span style=\"font-family:宋体;font-size:10.5pt;\">）</span><span style=\"font-size:10.5pt;\">25</span><span style=\"font-family:宋体;font-size:10.5pt;\"></span>\r\n</p>\r\n<p style=\"text-indent:15.75pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:宋体;font-size:10.5pt;\">（</span><span style=\"font-family:宋体;font-size:10.5pt;\">C</span><span style=\"font-family:宋体;font-size:10.5pt;\">）</span><span style=\"font-size:10.5pt;\">4000</span><span style=\"font-family:宋体;font-size:10.5pt;\"></span>\r\n</p>\r\n<p style=\"text-indent:15.75pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:宋体;font-size:10.5pt;\">（</span><span style=\"font-family:宋体;font-size:10.5pt;\">D</span><span style=\"font-family:宋体;font-size:10.5pt;\">）</span><span style=\"font-family:宋体;font-size:10.5pt;\">对于</span><span style=\"font-size:10.5pt;\">VARCHAR2</span><span style=\"font-family:宋体;font-size:10.5pt;\">列，必须指定列的长度</span><span style=\"font-size:10.5pt;\"></span>\r\n</p>\r\n<p style=\"text-indent:15.8pt;\" class=\"MsoNormal\">\r\n	<b><span style=\"font-family:宋体;font-size:10.5pt;\"></span></b>\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<b><span style=\"font-family:宋体;font-size:10.5pt;\">9</span></b><b><span style=\"font-family:宋体;font-size:10.5pt;\">、</span></b><span style=\"font-family:宋体;font-size:10.5pt;\">数据库系统与文件系统的主要区别是</span><span style=\"font-family:宋体;font-size:10.5pt;\">(<span><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span>)</span><span style=\"font-family:宋体;font-size:10.5pt;\">。</span><span style=\"font-family:宋体;font-size:10.5pt;\"></span>\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-family:宋体;font-size:10.5pt;\"></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:宋体;font-size:10.5pt;\">(A) </span><span style=\"font-family:宋体;font-size:10.5pt;\">数据库系统复杂，而文件系统简单</span><span style=\"font-family:宋体;font-size:10.5pt;\"></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:宋体;font-size:10.5pt;\">(B) </span><span style=\"font-family:宋体;font-size:10.5pt;\">文件系统不能解决数据冗余和数据独立性问题，而数据库系统可以解决</span><span style=\"font-family:宋体;font-size:10.5pt;\"></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:宋体;font-size:10.5pt;\">(C) </span><span style=\"font-family:宋体;font-size:10.5pt;\">文件系统只能管理程序文件，而数据库系统能够管理各种类型的文件</span><span style=\"font-family:宋体;font-size:10.5pt;\"></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:宋体;font-size:10.5pt;\">(D) </span><span style=\"font-family:宋体;font-size:10.5pt;\">文件系统管理的数据量较少，而数据库系统可以管理庞大的数据量</span><span style=\"font-family:宋体;font-size:10.5pt;\"></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:宋体;font-size:10.5pt;\"></span>\r\n</p>\r\n<p style=\"text-indent:0mm;\" class=\"MsoNormalIndent\">\r\n	<b><span style=\"font-family:宋体;\">10</span></b><b><span style=\"font-family:宋体;\">、</span></b><span style=\"font-family:宋体;\">Select</span><span style=\"font-family:宋体;\">语句的执行顺序是</span><span style=\"font-family:宋体;\">(<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span>)</span>\r\n</p>\r\n<p class=\"MsoNormalIndent\">\r\n	<span style=\"font-family:宋体;\">(1)select (2)from (3)where (4)group by (5)having (6)order by </span>\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:10.5pt;\"></span>\r\n</p>\r\n<p class=\"MsoNormalIndent\">\r\n	<span style=\"font-family:宋体;\">(A)</span><span> 123456;</span><span style=\"font-family:宋体;\"></span>\r\n</p>\r\n<p class=\"MsoNormalIndent\">\r\n	<span style=\"font-family:宋体;\">(B)</span><span> 234561;</span><span style=\"font-family:宋体;\"></span>\r\n</p>\r\n<p class=\"MsoNormalIndent\">\r\n	<span style=\"font-family:宋体;\">(C)</span><span> 234516;</span><span style=\"font-family:宋体;\"> </span>\r\n</p>\r\n<p class=\"MsoNormalIndent\">\r\n	<span style=\"font-family:宋体;\">(D) </span><span>124563</span><b><span style=\"font-family:宋体;\"></span></b>\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<b><span style=\"font-size:10.5pt;\"></span></b>\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-family:\'Courier New\';font-size:10.5pt;\"></span>\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-family:\'Courier New\';font-size:10.5pt;\"></span>\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-family:\'Courier New\';font-size:10.5pt;\"></span>\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:10.5pt;\"></span>\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<b><span style=\"font-family:宋体;font-size:10.5pt;\">三、填空题（每题</span></b><b><span style=\"font-family:宋体;font-size:10.5pt;\">4</span></b><b><span style=\"font-family:宋体;font-size:10.5pt;\">分</span></b><b><span style=\"font-family:宋体;font-size:10.5pt;\">/</span></b><b><span style=\"font-family:宋体;font-size:10.5pt;\">共</span></b><b><span style=\"font-family:宋体;font-size:10.5pt;\">20</span></b><b><span style=\"font-family:宋体;font-size:10.5pt;\">分）</span></b><b><span style=\"font-family:宋体;font-size:10.5pt;\"></span></b>\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<b><span style=\"font-family:宋体;font-size:10.5pt;\"></span></b>\r\n</p>\r\n<p class=\"MsoBodyText\">\r\n	<b><span>1</span></b><b><span style=\"font-family:宋体;\">、</span></b><span style=\"font-family:宋体;\">在数据库中，用于事务提交和回滚的</span><span style=\"font-family:宋体;\">SQL</span><span style=\"font-family:宋体;\">语句分别是</span><span style=\"font-family:宋体;\">________</span><span style=\"font-family:宋体;\">和</span><span style=\"font-family:宋体;\">________</span><span style=\"font-family:宋体;\">。</span><span style=\"font-family:宋体;\"></span>\r\n</p>\r\n<p class=\"MsoBodyText\">\r\n	<span style=\"font-family:宋体;\"></span>\r\n</p>\r\n<p class=\"MsoBodyText\">\r\n	<b><span>2</span></b><b><span style=\"font-family:宋体;\">、</span></b><span>C</span><span style=\"font-family:宋体;\">语言源程序文件的扩展名是</span><span>______</span><span style=\"font-family:宋体;\">，经过编译、连接后，生成文件的扩展名是</span><span>______</span><span style=\"font-family:宋体;\">。</span><span></span>\r\n</p>\r\n<p class=\"MsoBodyText\">\r\n	<span style=\"font-family:宋体;\"></span>\r\n</p>\r\n<p class=\"MsoBodyText\">\r\n	<b><span>3</span></b><b><span style=\"font-family:宋体;\">、</span></b><span style=\"font-family:宋体;\">在</span><span>C</span><span style=\"font-family:宋体;\">语言中，用</span><span>______</span><span style=\"font-family:宋体;\">表示逻辑</span><span>“</span><span style=\"font-family:宋体;\">真</span><span>”</span><span style=\"font-family:宋体;\">值。</span><span></span>\r\n</p>\r\n<p class=\"MsoBodyText\">\r\n	<span></span>\r\n</p>\r\n<p class=\"MsoBodyText\">\r\n	<b><span>4</span></b><b><span style=\"font-family:宋体;\">、</span></b><span style=\"font-family:宋体;color:black;\">通过</span><span style=\"font-family:宋体;color:black;\">______</span><span style=\"font-family:宋体;color:black;\">命令编译</span><span style=\"font-family:宋体;color:black;\">Java</span><span style=\"font-family:宋体;color:black;\">源程序，</span><span style=\"font-family:宋体;color:black;\">______</span><span style=\"font-family:宋体;color:black;\">命令执行</span><span style=\"font-family:宋体;color:black;\">Java</span><span style=\"font-family:宋体;color:black;\">程序</span><span style=\"font-family:宋体;color:black;\"></span>\r\n</p>\r\n<p class=\"MsoBodyText\">\r\n	<span style=\"font-family:宋体;color:black;\"></span>\r\n</p>\r\n<p class=\"MsoBodyText\">\r\n	<b><span>5</span></b><b><span style=\"font-family:宋体;\">、</span></b><span style=\"font-family:宋体;\">在</span><span>Java</span><span style=\"font-family:宋体;\">中</span><span style=\"font-family:宋体;\">将</span><span>double</span><span style=\"font-family:宋体;\">数据类型的变量</span><span>x</span><span style=\"font-family:宋体;\">的数值，强制转换为</span><span>int</span><span style=\"font-family:宋体;\">类型的数据。然后赋给</span><span>int</span><span style=\"font-family:宋体;\">数据类型的变量</span><span>y</span><span style=\"font-family:宋体;\">的表达式为</span><span>_______________________________________</span><span style=\"font-family:宋体;\">。</span><u><span></span></u>\r\n</p>\r\n<p>\r\n	&nbsp;\r\n</p>', '0', null, null, null, null, null, null, null);
INSERT INTO `tb_praxis` VALUES ('1491294632726', '&nbsp;\r\n<p style=\"text-align:center;\" class=\"MsoNormal\" align=\"center\">\r\n	<b><span style=\"line-height:150%;font-family:宋体;font-size:22pt;\">笔试题</span></b><b><span style=\"font-family:宋体;\"></span></b>\r\n</p>\r\n<div>\r\n	<p class=\"MsoNormal\">\r\n		<b><span style=\"line-height:150%;font-family:宋体;\">姓名：</span></b><b><span style=\"line-height:150%;font-family:宋体;\"><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span></b><b><span style=\"line-height:150%;font-family:宋体;\">学校：</span></b><b><span style=\"line-height:150%;font-family:宋体;\"><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span></b><b><span style=\"line-height:150%;font-family:宋体;\">专业：</span></b><b><span style=\"line-height:150%;font-family:宋体;\"></span></b>\r\n	</p>\r\n</div>\r\n<p class=\"MsoNormal\">\r\n	<b><span style=\"font-family:宋体;color:black;font-size:10.5pt;\"></span></b>\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<b><span style=\"font-family:宋体;color:black;font-size:10.5pt;\">一、选择题</span></b><b><span style=\"font-family:宋体;color:black;font-size:10.5pt;\">(</span></b><b><span style=\"font-family:宋体;color:black;font-size:10.5pt;\">每题</span></b><b><span style=\"font-family:宋体;color:black;font-size:10.5pt;\">4</span></b><b><span style=\"font-family:宋体;color:black;font-size:10.5pt;\">分</span></b><b><span style=\"font-family:宋体;color:black;font-size:10.5pt;\">/</span></b><b><span style=\"font-family:宋体;color:black;font-size:10.5pt;\">共</span></b><b><span style=\"font-family:宋体;color:black;font-size:10.5pt;\">40</span></b><b><span style=\"font-family:宋体;color:black;font-size:10.5pt;\">分</span></b><b><span style=\"font-family:宋体;color:black;font-size:10.5pt;\">)</span></b>\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<b><span style=\"font-family:\'ˎ̥\',\'serif\';color:black;font-size:10.5pt;\">1</span></b><b><span style=\"font-family:宋体;color:black;font-size:10.5pt;\">、</span></b><span style=\"font-family:宋体;font-size:10.5pt;\">请问，为了使一个名为</span><span style=\"font-family:宋体;font-size:10.5pt;\">MyClass</span><span style=\"font-family:宋体;font-size:10.5pt;\">的</span><span style=\"font-family:宋体;font-size:10.5pt;\">public</span><span style=\"font-family:宋体;font-size:10.5pt;\">类成功编译，需满足以下哪些条件？</span><span style=\"font-family:宋体;font-size:10.5pt;\"></span>\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-family:宋体;font-size:10.5pt;\"></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormalIndent\">\r\n	<span style=\"font-family:宋体;\">请选择一个正确答案：</span><span style=\"font-family:宋体;\">(<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span>)</span><span style=\"font-family:宋体;\"></span>\r\n</p>\r\n<p style=\"text-indent:0mm;margin-left:21pt;\" class=\"MsoNormalIndent\">\r\n	<span style=\"font-family:宋体;\">(A) MyClass</span><span style=\"font-family:宋体;\">类中必须定义一个正确的</span><span style=\"font-family:宋体;\">main()</span><span style=\"font-family:宋体;\">方法。</span><span style=\"font-family:宋体;\"></span>\r\n</p>\r\n<p style=\"text-indent:0mm;margin-left:21pt;\" class=\"MsoNormalIndent\">\r\n	<span style=\"font-family:宋体;\">(B) MyClass</span><span style=\"font-family:宋体;\">类必须定义在</span><span style=\"font-family:宋体;\">MyClass.java</span><span style=\"font-family:宋体;\">源文件中。</span><span style=\"font-family:宋体;\"></span>\r\n</p>\r\n<p style=\"text-indent:0mm;margin-left:21pt;\" class=\"MsoNormalIndent\">\r\n	<span style=\"font-family:宋体;\">(C) MyClass</span><span style=\"font-family:宋体;\">类必须定义在</span><span style=\"font-family:宋体;\">MyClass</span><span style=\"font-family:宋体;\">包中。</span><span style=\"font-family:宋体;\"></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:宋体;font-size:10.5pt;\">(D) MyClass</span><span style=\"font-family:宋体;font-size:10.5pt;\">类必须被导入。</span><span style=\"font-family:宋体;font-size:10.5pt;\"></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:宋体;font-size:10.5pt;\"></span>\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<b><span style=\"font-family:宋体;font-size:10.5pt;\">2</span></b><b><span style=\"font-family:宋体;font-size:10.5pt;\">、</span></b><span style=\"font-family:宋体;font-size:10.5pt;\">以下叙述中正确的是：</span><span style=\"font-family:宋体;font-size:10.5pt;\">(<span><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span>)</span>\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-family:宋体;font-size:10.5pt;\"></span>\r\n</p>\r\n<p style=\"text-indent:20.25pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:宋体;font-size:10.5pt;\">(A)</span><span style=\"font-family:宋体;font-size:10.5pt;\">C</span><span style=\"font-family:宋体;font-size:10.5pt;\">语言比其他语言高级</span><span style=\"font-family:宋体;font-size:10.5pt;\">;</span>\r\n</p>\r\n<p style=\"text-indent:20.25pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:宋体;font-size:10.5pt;\">(B)</span><span style=\"font-family:宋体;font-size:10.5pt;\">C</span><span style=\"font-family:宋体;font-size:10.5pt;\">语言可以不用编译就能被计算机识别执行</span><span style=\"font-family:宋体;font-size:10.5pt;\">;</span>\r\n</p>\r\n<p style=\"text-indent:20.25pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:宋体;font-size:10.5pt;\">(C)</span><span style=\"font-family:宋体;font-size:10.5pt;\">C</span><span style=\"font-family:宋体;font-size:10.5pt;\">语言以接近英语国家的自然语言和数学语言作为语言的表达形式</span><span style=\"font-family:宋体;font-size:10.5pt;\">; </span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:宋体;font-size:10.5pt;\">(D)C</span><span style=\"font-family:宋体;font-size:10.5pt;\">语言出现的最晚，具有其他语言的一切优点</span><span style=\"font-family:宋体;font-size:10.5pt;\"></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormalIndent\">\r\n	<span style=\"font-family:宋体;\"></span>\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<b><span style=\"font-family:宋体;font-size:10.5pt;\">3</span></b><b><span style=\"font-family:宋体;font-size:10.5pt;\">、</span></b><span style=\"font-family:宋体;font-size:10.5pt;\">在</span><span style=\"font-family:宋体;font-size:10.5pt;\">C</span><span style=\"font-family:宋体;font-size:10.5pt;\">语言中将两个字符串连接起来组成一个字符串时，选用</span><span style=\"font-family:宋体;font-size:10.5pt;\">(<span><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span>)</span><span style=\"font-family:宋体;font-size:10.5pt;\">函数。</span><span style=\"font-family:宋体;font-size:10.5pt;\"></span>\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-family:宋体;font-size:10.5pt;\"></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormalIndent\">\r\n	<span style=\"font-family:宋体;\">(A) strlen( );</span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormalIndent\">\r\n	<span style=\"font-family:宋体;\">(B) strcpy( );</span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormalIndent\">\r\n	<span style=\"font-family:宋体;\">(C) strcat( );</span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormalIndent\">\r\n	<span style=\"font-family:宋体;\">(D) strcmp( );</span>\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:10.5pt;\"></span>\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<b><span style=\"font-family:宋体;font-size:10.5pt;\">4</span></b><b><span style=\"font-family:宋体;font-size:10.5pt;\">、</span></b><span style=\"font-family:宋体;font-size:10.5pt;\">给出以下</span><span style=\"font-family:宋体;font-size:10.5pt;\">Java</span><span style=\"font-family:宋体;font-size:10.5pt;\">代码，请问该程序的运行结果是什么？</span><span style=\"font-family:宋体;font-size:10.5pt;\"></span>\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-family:宋体;font-size:10.5pt;\"></span>\r\n</p>\r\n<div align=\"center\">\r\n	<table style=\"width:327pt;background:#e6e6e6;\" class=\"MsoNormalTable ke-zeroborder\" border=\"0\" cellpadding=\"0\" width=\"436\">\r\n		<tbody>\r\n			<tr>\r\n				<td valign=\"top\" width=\"432\">\r\n					<div align=\"center\">\r\n						<table style=\"width:327pt;background:#e6e6e6;\" class=\"MsoNormalTable ke-zeroborder\" border=\"0\" cellpadding=\"0\" width=\"436\">\r\n							<tbody>\r\n								<tr>\r\n									<td width=\"432\">\r\n										<p class=\"MsoNormal\">\r\n											<span style=\"font-family:宋体;font-size:10.5pt;\">class Example {</span>\r\n										</p>\r\n										<p style=\"text-indent:15.75pt;\" class=\"MsoNormal\">\r\n											<span style=\"font-family:宋体;font-size:10.5pt;\">public static void main(String args[]){</span>\r\n										</p>\r\n										<p style=\"text-indent:33.8pt;\" class=\"MsoNormal\">\r\n											<span style=\"font-family:宋体;font-size:10.5pt;\">boolean b = true;</span>\r\n										</p>\r\n										<p style=\"text-indent:33.8pt;\" class=\"MsoNormal\">\r\n											<span style=\"font-family:宋体;font-size:10.5pt;\">System.out.println(b);</span>\r\n										</p>\r\n										<p style=\"text-indent:15.75pt;\" class=\"MsoNormal\">\r\n											<span style=\"font-family:宋体;font-size:10.5pt;\">}</span>\r\n										</p>\r\n										<p class=\"MsoNormal\">\r\n											<span style=\"font-family:宋体;font-size:10.5pt;\">}</span>\r\n										</p>\r\n									</td>\r\n								</tr>\r\n							</tbody>\r\n						</table>\r\n					</div>\r\n					<p class=\"MsoNormal\">\r\n						<span style=\"font-size:10.5pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n		</tbody>\r\n	</table>\r\n</div>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormalIndent\">\r\n	<span style=\"font-family:宋体;\"></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormalIndent\">\r\n	<span style=\"font-family:宋体;\">请选择一个正确答案：</span><span style=\"font-family:宋体;\">(<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span>)</span>\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span></span>\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormalIndent\">\r\n	<span style=\"font-family:宋体;\">(A) </span><span style=\"font-family:宋体;\">打印输出：</span><span style=\"font-family:宋体;\">true</span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormalIndent\">\r\n	<span style=\"font-family:宋体;\">(B)&nbsp;</span><span style=\"font-family:宋体;\">打印输出：</span><span style=\"font-family:宋体;\">1</span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormalIndent\">\r\n	<span style=\"font-family:宋体;\">(C)&nbsp;</span><span style=\"font-family:宋体;\">编译错误，因为</span><span style=\"font-family:宋体;\">System.out.println()</span><span style=\"font-family:宋体;\">方法的参数只能为整数。</span><span style=\"font-family:宋体;\"></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormalIndent\">\r\n	<span style=\"font-family:宋体;\">(D)&nbsp;</span><span style=\"font-family:宋体;\">无内容输出。</span><span style=\"font-family:宋体;\"></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:宋体;font-size:10.5pt;\">(E)&nbsp;</span><span style=\"font-family:宋体;font-size:10.5pt;\">编译错误，如果把“</span><span style=\"font-family:宋体;font-size:10.5pt;\">true</span><span style=\"font-family:宋体;font-size:10.5pt;\">”改变为“</span><span style=\"font-family:宋体;font-size:10.5pt;\">TRUE</span><span style=\"font-family:宋体;font-size:10.5pt;\">”，则可使代码编译通过。</span><span style=\"font-family:宋体;font-size:10.5pt;\"></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:宋体;font-size:10.5pt;\"></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:宋体;font-size:10.5pt;\"></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:宋体;font-size:10.5pt;\"></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:宋体;font-size:10.5pt;\"></span>\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<b><span style=\"font-family:宋体;font-size:10.5pt;\">5</span></b><b><span style=\"font-family:宋体;font-size:10.5pt;\">、</span></b><span style=\"font-family:宋体;font-size:10.5pt;\">C</span><span style=\"font-family:宋体;font-size:10.5pt;\">语言中用于结构化程序设计的三种基本结构是</span><span style=\"font-family:宋体;font-size:10.5pt;\">(<span><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span>)</span><span style=\"font-family:宋体;font-size:10.5pt;\"></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormalIndent\">\r\n	<span style=\"font-family:宋体;\"></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormalIndent\">\r\n	<span style=\"font-family:宋体;\">(A) </span><span style=\"font-family:宋体;\">顺序结构、选择结构、循环结构</span><span style=\"font-family:宋体;\">;</span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormalIndent\">\r\n	<span style=\"font-family:宋体;\">(B)</span><span> </span><span style=\"font-family:宋体;\">if</span><span style=\"font-family:宋体;\">、</span><span style=\"font-family:宋体;\">switch</span><span style=\"font-family:宋体;\">、</span><span style=\"font-family:宋体;\">break;</span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormalIndent\">\r\n	<span style=\"font-family:宋体;\">(C)</span><span> </span><span style=\"font-family:宋体;\">for</span><span style=\"font-family:宋体;\">、</span><span style=\"font-family:宋体;\">while</span><span style=\"font-family:宋体;\">、</span><span style=\"font-family:宋体;\">do-while;</span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormalIndent\">\r\n	<span style=\"font-family:宋体;\">(D)</span><span> </span><span style=\"font-family:宋体;\">if</span><span style=\"font-family:宋体;\">、</span><span style=\"font-family:宋体;\">for</span><span style=\"font-family:宋体;\">、</span><span style=\"font-family:宋体;\">continue</span>\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-family:宋体;font-size:10.5pt;\"></span>\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<b><span style=\"font-family:宋体;font-size:10.5pt;\">6</span></b><b><span style=\"font-family:宋体;font-size:10.5pt;\">、</span></b><span style=\"font-family:宋体;font-size:10.5pt;\">有以下程序</span><span style=\"font-family:宋体;font-size:10.5pt;\">:</span>\r\n</p>\r\n<div align=\"center\">\r\n	<table style=\"width:327pt;background:#e6e6e6;\" class=\"MsoNormalTable ke-zeroborder\" border=\"0\" cellpadding=\"0\" width=\"436\">\r\n		<tbody>\r\n			<tr>\r\n				<td valign=\"top\" width=\"432\">\r\n					<div align=\"center\">\r\n						<table style=\"width:327pt;background:#e6e6e6;\" class=\"MsoNormalTable ke-zeroborder\" border=\"0\" cellpadding=\"0\" width=\"436\">\r\n							<tbody>\r\n								<tr>\r\n									<td width=\"432\">\r\n										<p class=\"MsoNormal\">\r\n											<span style=\"font-family:宋体;font-size:10.5pt;\">main()</span>\r\n										</p>\r\n										<p style=\"text-indent:26.25pt;\" class=\"MsoNormal\">\r\n											<span style=\"font-family:宋体;font-size:10.5pt;\">{ </span>\r\n										</p>\r\n										<p style=\"text-indent:42pt;\" class=\"MsoNormal\">\r\n											<span style=\"font-family:宋体;font-size:10.5pt;\">int i;</span>\r\n										</p>\r\n										<p style=\"text-indent:42pt;\" class=\"MsoNormal\">\r\n											<span style=\"font-family:宋体;font-size:10.5pt;\">for(i=0;i&lt;3;i++)</span>\r\n										</p>\r\n										<p style=\"text-indent:57.75pt;\" class=\"MsoNormal\">\r\n											<span style=\"font-family:宋体;font-size:10.5pt;\">switch(i)</span>\r\n										</p>\r\n										<p style=\"text-indent:63pt;\" class=\"MsoNormal\">\r\n											<span style=\"font-family:宋体;font-size:10.5pt;\">{ case 1: printf(\"%d\",i);</span>\r\n										</p>\r\n										<p style=\"text-indent:73.5pt;\" class=\"MsoNormal\">\r\n											<span style=\"font-family:宋体;font-size:10.5pt;\">case 2: printf(\"%d\",i);</span>\r\n										</p>\r\n										<p style=\"text-indent:73.5pt;\" class=\"MsoNormal\">\r\n											<span style=\"font-family:宋体;font-size:10.5pt;\">default: printf(\"%d\",i);</span>\r\n										</p>\r\n										<p style=\"text-indent:63pt;\" class=\"MsoNormal\">\r\n											<span style=\"font-family:宋体;font-size:10.5pt;\">}</span>\r\n										</p>\r\n										<p style=\"text-indent:21pt;\" class=\"MsoNormal\">\r\n											<span style=\"font-family:宋体;font-size:10.5pt;\">}</span>\r\n										</p>\r\n									</td>\r\n								</tr>\r\n							</tbody>\r\n						</table>\r\n					</div>\r\n					<p class=\"MsoNormal\">\r\n						<span style=\"font-size:10.5pt;\"></span>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n		</tbody>\r\n	</table>\r\n</div>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-family:宋体;font-size:10.5pt;\"><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span><span style=\"font-family:宋体;font-size:10.5pt;\">执行后输出结果是</span><span style=\"font-family:宋体;font-size:10.5pt;\">:</span><span style=\"font-family:宋体;font-size:10.5pt;\"> (<span><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span>)</span><span style=\"font-family:宋体;font-size:10.5pt;\"></span>\r\n</p>\r\n<p style=\"margin-left:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:宋体;font-size:10.5pt;\">(A) 011122;</span>\r\n</p>\r\n<p style=\"margin-left:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:宋体;font-size:10.5pt;\">(B) 012 ;</span>\r\n</p>\r\n<p style=\"margin-left:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:宋体;font-size:10.5pt;\">(C) 012020 ;</span>\r\n</p>\r\n<p style=\"margin-left:18pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:宋体;font-size:10.5pt;\">(D) 120</span><span style=\"font-family:宋体;font-size:10.5pt;\"></span>\r\n</p>\r\n<p style=\"text-indent:0mm;\" class=\"MsoNormalIndent\">\r\n	<b><span style=\"font-family:宋体;\">7</span></b><b><span style=\"font-family:宋体;\">、</span></b><span style=\"font-family:宋体;\">在</span><span> Java </span><span style=\"font-family:宋体;\">中，由</span><span>Java</span><span style=\"font-family:宋体;\">编译器自动导入，而无需在程序中用</span><span>import</span><span style=\"font-family:宋体;\">导入的包是<span style=\"background:white;color:black;\">：</span></span><span style=\"font-family:宋体;\">(<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span>)</span><span> </span>\r\n</p>\r\n<p style=\"text-indent:0mm;\" class=\"MsoNormalIndent\">\r\n	<span></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormalIndent\">\r\n	<span style=\"font-family:宋体;\">(A)</span><span> java.applet <span>&nbsp;</span></span>\r\n</p>\r\n<p style=\"text-indent:20.05pt;\" class=\"MsoNormalIndent\">\r\n	<span style=\"font-family:宋体;\">(B) </span><span>java.awt </span><span style=\"font-family:宋体;\"></span>\r\n</p>\r\n<p style=\"text-indent:20.25pt;\" class=\"MsoNormalIndent\">\r\n	<span style=\"font-family:宋体;\">(C) </span><span>java.util </span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormalIndent\">\r\n	<span style=\"font-family:宋体;\">(D)</span><span> java.lang </span>\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:10.5pt;\"></span>\r\n</p>\r\n<p style=\"text-indent:0mm;\" class=\"MsoNormalIndent\">\r\n	<b><span style=\"font-family:宋体;\">8</span></b><b><span style=\"font-family:宋体;\">、</span></b><span style=\"font-family:宋体;\">在</span><span style=\"font-family:宋体;\">Oracle</span><span style=\"font-family:宋体;\">数据库中</span><span style=\"font-family:宋体;\">创建表时，一个</span><span style=\"font-family:宋体;\">VARCHAR2</span><span style=\"font-family:宋体;\">的列没有指定长度，它的缺省长度是</span><span style=\"font-family:宋体;\">(<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span>)</span><span style=\"font-family:宋体;\">？</span><span style=\"font-family:宋体;\"></span>\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-family:宋体;font-size:10.5pt;\"></span>\r\n</p>\r\n<p style=\"text-indent:15.75pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:宋体;font-size:10.5pt;\">（</span><span style=\"font-family:宋体;font-size:10.5pt;\">A</span><span style=\"font-family:宋体;font-size:10.5pt;\">）</span><span style=\"font-size:10.5pt;\">1</span><span style=\"font-family:宋体;font-size:10.5pt;\"></span>\r\n</p>\r\n<p style=\"text-indent:15.75pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:宋体;font-size:10.5pt;\">（</span><span style=\"font-family:宋体;font-size:10.5pt;\">B</span><span style=\"font-family:宋体;font-size:10.5pt;\">）</span><span style=\"font-size:10.5pt;\">25</span><span style=\"font-family:宋体;font-size:10.5pt;\"></span>\r\n</p>\r\n<p style=\"text-indent:15.75pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:宋体;font-size:10.5pt;\">（</span><span style=\"font-family:宋体;font-size:10.5pt;\">C</span><span style=\"font-family:宋体;font-size:10.5pt;\">）</span><span style=\"font-size:10.5pt;\">4000</span><span style=\"font-family:宋体;font-size:10.5pt;\"></span>\r\n</p>\r\n<p style=\"text-indent:15.75pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:宋体;font-size:10.5pt;\">（</span><span style=\"font-family:宋体;font-size:10.5pt;\">D</span><span style=\"font-family:宋体;font-size:10.5pt;\">）</span><span style=\"font-family:宋体;font-size:10.5pt;\">对于</span><span style=\"font-size:10.5pt;\">VARCHAR2</span><span style=\"font-family:宋体;font-size:10.5pt;\">列，必须指定列的长度</span><span style=\"font-size:10.5pt;\"></span>\r\n</p>\r\n<p style=\"text-indent:15.8pt;\" class=\"MsoNormal\">\r\n	<b><span style=\"font-family:宋体;font-size:10.5pt;\"></span></b>\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<b><span style=\"font-family:宋体;font-size:10.5pt;\">9</span></b><b><span style=\"font-family:宋体;font-size:10.5pt;\">、</span></b><span style=\"font-family:宋体;font-size:10.5pt;\">数据库系统与文件系统的主要区别是</span><span style=\"font-family:宋体;font-size:10.5pt;\">(<span><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span>)</span><span style=\"font-family:宋体;font-size:10.5pt;\">。</span><span style=\"font-family:宋体;font-size:10.5pt;\"></span>\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-family:宋体;font-size:10.5pt;\"></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:宋体;font-size:10.5pt;\">(A) </span><span style=\"font-family:宋体;font-size:10.5pt;\">数据库系统复杂，而文件系统简单</span><span style=\"font-family:宋体;font-size:10.5pt;\"></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:宋体;font-size:10.5pt;\">(B) </span><span style=\"font-family:宋体;font-size:10.5pt;\">文件系统不能解决数据冗余和数据独立性问题，而数据库系统可以解决</span><span style=\"font-family:宋体;font-size:10.5pt;\"></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:宋体;font-size:10.5pt;\">(C) </span><span style=\"font-family:宋体;font-size:10.5pt;\">文件系统只能管理程序文件，而数据库系统能够管理各种类型的文件</span><span style=\"font-family:宋体;font-size:10.5pt;\"></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:宋体;font-size:10.5pt;\">(D) </span><span style=\"font-family:宋体;font-size:10.5pt;\">文件系统管理的数据量较少，而数据库系统可以管理庞大的数据量</span><span style=\"font-family:宋体;font-size:10.5pt;\"></span>\r\n</p>\r\n<p style=\"text-indent:21pt;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:宋体;font-size:10.5pt;\"></span>\r\n</p>\r\n<p style=\"text-indent:0mm;\" class=\"MsoNormalIndent\">\r\n	<b><span style=\"font-family:宋体;\">10</span></b><b><span style=\"font-family:宋体;\">、</span></b><span style=\"font-family:宋体;\">Select</span><span style=\"font-family:宋体;\">语句的执行顺序是</span><span style=\"font-family:宋体;\">(<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span>)</span>\r\n</p>\r\n<p class=\"MsoNormalIndent\">\r\n	<span style=\"font-family:宋体;\">(1)select (2)from (3)where (4)group by (5)having (6)order by </span>\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:10.5pt;\"></span>\r\n</p>\r\n<p class=\"MsoNormalIndent\">\r\n	<span style=\"font-family:宋体;\">(A)</span><span> 123456;</span><span style=\"font-family:宋体;\"></span>\r\n</p>\r\n<p class=\"MsoNormalIndent\">\r\n	<span style=\"font-family:宋体;\">(B)</span><span> 234561;</span><span style=\"font-family:宋体;\"></span>\r\n</p>\r\n<p class=\"MsoNormalIndent\">\r\n	<span style=\"font-family:宋体;\">(C)</span><span> 234516;</span><span style=\"font-family:宋体;\"> </span>\r\n</p>\r\n<p class=\"MsoNormalIndent\">\r\n	<span style=\"font-family:宋体;\">(D) </span><span>124563</span><b><span style=\"font-family:宋体;\"></span></b>\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<b><span style=\"font-size:10.5pt;\"></span></b>\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-family:\'Courier New\';font-size:10.5pt;\"></span>\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-family:\'Courier New\';font-size:10.5pt;\"></span>\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-family:\'Courier New\';font-size:10.5pt;\"></span>\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:10.5pt;\"></span>\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<b><span style=\"font-family:宋体;font-size:10.5pt;\">三、填空题（每题</span></b><b><span style=\"font-family:宋体;font-size:10.5pt;\">4</span></b><b><span style=\"font-family:宋体;font-size:10.5pt;\">分</span></b><b><span style=\"font-family:宋体;font-size:10.5pt;\">/</span></b><b><span style=\"font-family:宋体;font-size:10.5pt;\">共</span></b><b><span style=\"font-family:宋体;font-size:10.5pt;\">20</span></b><b><span style=\"font-family:宋体;font-size:10.5pt;\">分）</span></b><b><span style=\"font-family:宋体;font-size:10.5pt;\"></span></b>\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<b><span style=\"font-family:宋体;font-size:10.5pt;\"></span></b>\r\n</p>\r\n<p class=\"MsoBodyText\">\r\n	<b><span>1</span></b><b><span style=\"font-family:宋体;\">、</span></b><span style=\"font-family:宋体;\">在数据库中，用于事务提交和回滚的</span><span style=\"font-family:宋体;\">SQL</span><span style=\"font-family:宋体;\">语句分别是</span><span style=\"font-family:宋体;\">________</span><span style=\"font-family:宋体;\">和</span><span style=\"font-family:宋体;\">________</span><span style=\"font-family:宋体;\">。</span><span style=\"font-family:宋体;\"></span>\r\n</p>\r\n<p class=\"MsoBodyText\">\r\n	<span style=\"font-family:宋体;\"></span>\r\n</p>\r\n<p class=\"MsoBodyText\">\r\n	<b><span>2</span></b><b><span style=\"font-family:宋体;\">、</span></b><span>C</span><span style=\"font-family:宋体;\">语言源程序文件的扩展名是</span><span>______</span><span style=\"font-family:宋体;\">，经过编译、连接后，生成文件的扩展名是</span><span>______</span><span style=\"font-family:宋体;\">。</span><span></span>\r\n</p>\r\n<p class=\"MsoBodyText\">\r\n	<span style=\"font-family:宋体;\"></span>\r\n</p>\r\n<p class=\"MsoBodyText\">\r\n	<b><span>3</span></b><b><span style=\"font-family:宋体;\">、</span></b><span style=\"font-family:宋体;\">在</span><span>C</span><span style=\"font-family:宋体;\">语言中，用</span><span>______</span><span style=\"font-family:宋体;\">表示逻辑</span><span>“</span><span style=\"font-family:宋体;\">真</span><span>”</span><span style=\"font-family:宋体;\">值。</span><span></span>\r\n</p>\r\n<p class=\"MsoBodyText\">\r\n	<span></span>\r\n</p>\r\n<p class=\"MsoBodyText\">\r\n	<b><span>4</span></b><b><span style=\"font-family:宋体;\">、</span></b><span style=\"font-family:宋体;color:black;\">通过</span><span style=\"font-family:宋体;color:black;\">______</span><span style=\"font-family:宋体;color:black;\">命令编译</span><span style=\"font-family:宋体;color:black;\">Java</span><span style=\"font-family:宋体;color:black;\">源程序，</span><span style=\"font-family:宋体;color:black;\">______</span><span style=\"font-family:宋体;color:black;\">命令执行</span><span style=\"font-family:宋体;color:black;\">Java</span><span style=\"font-family:宋体;color:black;\">程序</span><span style=\"font-family:宋体;color:black;\"></span>\r\n</p>\r\n<p class=\"MsoBodyText\">\r\n	<span style=\"font-family:宋体;color:black;\"></span>\r\n</p>\r\n<p class=\"MsoBodyText\">\r\n	<b><span>5</span></b><b><span style=\"font-family:宋体;\">、</span></b><span style=\"font-family:宋体;\">在</span><span>Java</span><span style=\"font-family:宋体;\">中</span><span style=\"font-family:宋体;\">将</span><span>double</span><span style=\"font-family:宋体;\">数据类型的变量</span><span>x</span><span style=\"font-family:宋体;\">的数值，强制转换为</span><span>int</span><span style=\"font-family:宋体;\">类型的数据。然后赋给</span><span>int</span><span style=\"font-family:宋体;\">数据类型的变量</span><span>y</span><span style=\"font-family:宋体;\">的表达式为</span><span>_______________________________________</span><span style=\"font-family:宋体;\">。</span><u><span></span></u>\r\n</p>\r\n<p>\r\n	&nbsp;\r\n</p>', '0', null, null, null, null, null, null, null);
INSERT INTO `tb_praxis` VALUES ('1491399581935', '<p>\n	<p style=\"background:#F2F9FF;\">\n		<span><a href=\"http://lib.csdn.net/base/javaee\" target=\"_blank\"><b>hibernate</b></a></span>&nbsp;中如果直接使用<span></span>\n	</p>\n	<p style=\"background:#F2F9FF;\">\n		Session.update(Object\no);\n	</p>\n	<p style=\"background:#F2F9FF;\">\n		会把这个表中的所有字段更新一遍。\n	</p>\n	<p style=\"background:#F2F9FF;\">\n		比如：<span></span>\n	</p>\n	<p style=\"background:#F2F9FF;\">\n		view plaincopy\nto clipboardprint?<br />\npublic class TeacherTest {<br />\n@Test<br />\npublic void update(){<br />\nSession session = HibernateUitl.getSessionFactory().getCurrentSession();<br />\nsession.beginTransaction();<br />\nTeacher t = (Teacher) session.get(Teacher.class, 3);<br />\nt.setName(“<span>yangtb2</span>″<span>);<br />\nsession.update(t);</span>\n	</p>\n	<p style=\"background:#F2F9FF;\">\n		session.getTransaction().commit();<br />\n}<br />\n}<br />\npublic class TeacherTest {<br />\n@Test<br />\npublic void update(){<br />\nSession session = HibernateUitl.getSessionFactory().getCurrentSession();<br />\nsession.beginTransaction();<br />\nTeacher t = (Teacher) session.get(Teacher.class, 3);<br />\nt.setName(“<span>yangtb2</span>″<span>);<br />\nsession.update(t);</span>\n	</p>\n	<p style=\"background:#F2F9FF;\">\n		session.getTransaction().commit();<br />\n}<br />\n}\n	</p>\n	<p style=\"background:#F2F9FF;\">\n		<span class=\"hl1\">Hibernate</span>&nbsp;执行的<span>SQL</span>语句：<span></span>\n	</p>\n	<p style=\"background:#F2F9FF;\">\n		view plaincopy\nto clipboardprint?<br />\n<span class=\"hl1\">Hibernate</span>:<br />\nupdate<br />\nTeacher<br />\nset<br />\nage=?,<br />\nbirthday=?,<br />\nname=?,<br />\ntitle=?<br />\nwhere<br />\nid=?<br />\n<span class=\"hl1\">Hibernate</span>:<br />\nupdate<br />\nTeacher<br />\nset<br />\nage=?,<br />\nbirthday=?,<br />\nname=?,<br />\ntitle=?<br />\nwhere<br />\nid=?\n	</p>\n	<p style=\"background:#F2F9FF;\">\n		我们只更改了<span>Name</span>属性，而<span class=\"hl1\"><span>Hibernate</span></span><span>&nbsp;</span>的<span><a href=\"http://cpro.baidu.com/cpro/ui/uijs.php?rs=1&amp;u=http%3A%2F%2Fblog%2Eknowsky%2Ecom%2F254835%2Ehtm&amp;p=baidu&amp;c=news&amp;n=10&amp;t=tpclicked3_hc&amp;q=sayyescpr&amp;k=sql&amp;k0=hibernate&amp;kdi0=8&amp;k1=sql&amp;kdi1=8&amp;k2=xml&amp;kdi2=8&amp;k3=%CA%FD%BE%DD%BF%E2&amp;kdi3=8&amp;k4=%B1%EA%C7%A9&amp;kdi4=8&amp;sid=7a6780f6698aed07&amp;ch=0&amp;tu=u1366390&amp;jk=22b0042fa1eefe2e&amp;cf=29&amp;rb=0&amp;fv=15&amp;stid=9&amp;urlid=0&amp;luki=2&amp;seller_id=1&amp;di=8\" target=\"_blank\">sql</a></span>语句 把所有字段都更改了一次。<span></span>\n	</p>\n	<p style=\"background:#F2F9FF;\">\n		这样要是我们有字段是文本类型，这个类型存储的内容是几千，几万字，这样效率会很低。<span></span>\n	</p>\n	<p style=\"background:#F2F9FF;\">\n		那么怎么只更改我们更新的字段呢？<span></span>\n	</p>\n	<p style=\"background:#F2F9FF;\">\n		有三中方法：<span></span>\n	</p>\n	<p style=\"background:#F2F9FF;\">\n		1.XML中设置<span>property&nbsp;</span><span><a href=\"http://cpro.baidu.com/cpro/ui/uijs.php?rs=1&amp;u=http%3A%2F%2Fblog%2Eknowsky%2Ecom%2F254835%2Ehtm&amp;p=baidu&amp;c=news&amp;n=10&amp;t=tpclicked3_hc&amp;q=sayyescpr&amp;k=%B1%EA%C7%A9&amp;k0=hibernate&amp;kdi0=8&amp;k1=sql&amp;kdi1=8&amp;k2=xml&amp;kdi2=8&amp;k3=%CA%FD%BE%DD%BF%E2&amp;kdi3=8&amp;k4=%B1%EA%C7%A9&amp;kdi4=8&amp;sid=7a6780f6698aed07&amp;ch=0&amp;tu=u1366390&amp;jk=22b0042fa1eefe2e&amp;cf=29&amp;rb=0&amp;fv=15&amp;stid=9&amp;urlid=0&amp;luki=5&amp;seller_id=1&amp;di=8\" target=\"_blank\">标签</a></span>&nbsp;update =\n“<span>false</span>” ，如下：我们设置<span> age </span>这个属性在更改中不做更改<span></span>\n	</p>\n	<p style=\"background:#F2F9FF;\">\n		view plaincopy\nto clipboardprint?<br />\n&lt;property name=”<span>age</span>”<span> update=</span>”<span>false</span>”<span>&gt;&lt;/property&gt;<br />\n&lt;property name=</span>”<span>age</span>”<span> update=</span>”<span>false</span>”<span>&gt;&lt;/property&gt;</span>\n	</p>\n	<p style=\"background:#F2F9FF;\">\n		在<span>Annotation</span>中\n在属性<span>GET</span>方法上加上<span>@Column(updatable=false)</span>\n	</p>\n	<p style=\"background:#F2F9FF;\">\n		view plaincopy\nto clipboardprint?<br />\n@Column(updatable=false)<br />\npublic int getAge() {<br />\nreturn age;<br />\n}<br />\n@Column(updatable=false)<br />\npublic int getAge() {<br />\nreturn age;<br />\n}\n	</p>\n	<p style=\"background:#F2F9FF;\">\n		我们在执行<span> Update</span>方法会发现，<span>age </span>属性 不会被更改<span></span>\n	</p>\n	<p style=\"background:#F2F9FF;\">\n		view plaincopy\nto clipboardprint?<br />\n<span class=\"hl1\">Hibernate</span>:<br />\nupdate<br />\nTeacher<br />\nset<br />\nbirthday=?,<br />\nname=?,<br />\ntitle=?<br />\nwhere<br />\nid=?<br />\n<span class=\"hl1\">Hibernate</span>:<br />\nupdate<br />\nTeacher<br />\nset<br />\nbirthday=?,<br />\nname=?,<br />\ntitle=?<br />\nwhere<br />\nid=?\n	</p>\n	<p style=\"background:#F2F9FF;\">\n		缺点：不灵活····<span></span>\n	</p>\n	<p style=\"background:#F2F9FF;\">\n		2.第<span>2</span>种方法··使用<span>XML</span>中的<span> dynamic-update=</span>”<span>true</span>”<span></span>\n	</p>\n	<p style=\"background:#F2F9FF;\">\n		view plaincopy\nto clipboardprint?<br />\n&lt;class name=”<span>com.sccin.entity.Student</span>”<span> table=</span>”<span>student</span>”<span> dynamic-update=</span>”<span>true</span>”<span>&gt;<br />\n&lt;class name=</span>”<span>com.sccin.entity.Student</span>”<span> table=</span>”<span>student</span>”<span> dynamic-update=</span>”<span>true</span>”<span>&gt;</span>\n	</p>\n	<p style=\"background:#F2F9FF;\">\n		OK,这样就不需要在字段上设置了。<span></span>\n	</p>\n	<p style=\"background:#F2F9FF;\">\n		但这样的方法在<span>Annotation</span>中没有<span></span>\n	</p>\n	<p style=\"background:#F2F9FF;\">\n		3.第三种方式：使用<span>HQL</span>语句（灵活，方便）<span></span>\n	</p>\n	<p style=\"background:#F2F9FF;\">\n		使用<span>HQL</span>语句修改数据<span></span>\n	</p>\n	<p style=\"background:#F2F9FF;\">\n		view plaincopy\nto clipboardprint?<br />\npublic void update(){<br />\nSession session = HibernateUitl.getSessionFactory().getCurrentSession();<br />\nsession.beginTransaction();<br />\nQuery query = session.createQuery(“<span>update Teacher t set t.name = </span>‘<span>yangtianb</span>’<span> where id = 3</span>″<span>);<br />\nquery.executeUpdate();<br />\nsession.getTransaction().commit();<br />\n}<br />\npublic void update(){<br />\nSession session = HibernateUitl.getSessionFactory().getCurrentSession();<br />\nsession.beginTransaction();<br />\nQuery query = session.createQuery(</span>“<span>update Teacher t set\nt.name = </span>‘<span>yangtianb</span>’<span> where id =\n3</span>″<span>);<br />\nquery.executeUpdate();<br />\nsession.getTransaction().commit();<br />\n}</span>\n	</p>\n	<p style=\"background:#F2F9FF;\">\n		<span class=\"hl1\">Hibernate</span>&nbsp;执行的<span>SQL</span>语句：<span></span>\n	</p>\n	<p style=\"background:#F2F9FF;\">\n		view plaincopy\nto clipboardprint?<br />\n<span class=\"hl1\">Hibernate</span>:<br />\nupdate<br />\nTeacher<br />\nset<br />\nname=’<span>yangtianb</span>’<span><br />\nwhere<br />\nid=3<br />\n<span class=\"hl1\">Hibernate</span>:<br />\nupdate<br />\nTeacher<br />\nset<br />\nname=</span>’<span>yangtianb</span>’<span><br />\nwhere<br />\nid=3</span>\n	</p>\n	<p style=\"background:#F2F9FF;\">\n		这样就只更新了我们更新的字段······\n	</p>\n	<p class=\"MsoNormal\">\n		<span>&nbsp;</span>\n	</p>\n</p>', '0', null, null, null, null, null, null, null);
INSERT INTO `tb_praxis` VALUES ('1491481095727', '<br />\n<br />\n<p style=\"text-align:center;\" class=\"MsoNormal\" align=\"center\">\n	<b><span style=\"font-family:楷体_GB2312;font-size:22pt;\">《</span></b><span><b><span style=\"font-family: ;\" New?;font-size:22pt;?=\"\" Courier=\"\">Oracle-sql</span></b></span><span><b><span style=\"font-family:黑体;font-size:22pt;\">开发基础</span></b></span><span><b><span style=\"font-family:楷体_GB2312;font-size:22pt;\">》笔试试卷</span></b></span><span><b><span style=\"font-family:楷体_GB2312;font-size:22pt;\"></span></b></span> \n</p>\n<br />\n<br />\n<div style=\"padding:0mm 0mm 1pt;\">\n	<br />\n<br />\n	<p class=\"MsoNormal\">\n		<span><b><span style=\"font-family:宋体;\">姓名：</span><span><span>&nbsp;&nbsp;&nbsp;&nbsp; </span><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><span>&nbsp;&nbsp;&nbsp;</span></span></b></span><span><b><span style=\"font-family:宋体;\">班级：</span><span><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span></b></span><span><b><span style=\"font-family:宋体;\">满分：</span><span>100</span></b></span><span><b><span style=\"font-family:宋体;\">分</span><span><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><span>&nbsp;<br />\n</span></span></b></span><span><b><span style=\"font-family:宋体;\">时间：</span><span>90</span></b></span><span><b><span style=\"font-family:宋体;\">分钟</span></b></span><b><span></span></b> \n	</p>\n<br />\n<br />\n</div>\n<br />\n<br />\n<p class=\"MsoNormal\">\n	<span></span> \n</p>\n<br />\n<br />\n<p class=\"MsoNormal\">\n	<b><span style=\"font-family:宋体;font-size:12pt;\">一</span></b><b><span style=\"font-size:12pt;\">.</span></b><b><span style=\"font-family:宋体;font-size:12pt;\">填空题（本题共</span></b><b><span style=\"font-size:12pt;\">3</span></b><b><span style=\"font-family:宋体;font-size:12pt;\">道小题，每空</span></b><b><span style=\"font-size:12pt;\">2</span></b><b><span style=\"font-family:宋体;font-size:12pt;\">分，共</span></b><b><span style=\"font-size:12pt;\">10</span></b><b><span style=\"font-family:宋体;font-size:12pt;\">分）</span></b><b><span style=\"font-size:12pt;\"></span></b> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>1</span><span style=\"font-family:宋体;\">．</span><br />\n<span style=\"font-family:宋体;\">在</span><span>ORACLE</span><span style=\"font-family:宋体;\">中，用于事务提交和回滚的</span><span>PL/SQL</span><span style=\"font-family:宋体;\">语句分别是</span><span>commit</span><span style=\"font-family:宋体;\">和</span><span>_rollback__</span><span style=\"font-family:宋体;\">。</span><span></span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>2</span><span style=\"font-family:宋体;\">．</span><span> SELECT TO_CHAR(ADD_MONTHS(TO_DATE(</span><span style=\"font-family:宋体;\">‘</span><span>20081210</span><span style=\"font-family:宋体;\">’</span><span>,</span><span style=\"font-family:宋体;\">’</span><span>YYYYMMDD</span><span style=\"font-family:宋体;\">’</span><span>),5)+2,</span><span style=\"font-family:宋体;\">’</span><span>YYYYMMDD</span><span style=\"font-family:宋体;\">’</span><span>) FROM DUAL;</span><span style=\"font-family:宋体;\">的运行结果是（）。</span><span></span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>3</span><span style=\"font-family:宋体;\">．</span><br />\n<span style=\"font-family:宋体;\">数据库按物理上结构必须由</span><span>___</span><span style=\"font-family:宋体;\">、</span><span>___</span><span style=\"font-family:宋体;\">和</span><span>___</span><span style=\"font-family:宋体;\">组成。</span><span></span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<b><span style=\"font-family:宋体;font-size:12pt;\">二</span></b><b><span style=\"font-size:12pt;\">.</span></b><b><span style=\"font-family:宋体;font-size:12pt;\">单选题（本题共</span></b><b><span style=\"font-size:12pt;\">30</span></b><b><span style=\"font-family:宋体;font-size:12pt;\">道小题，每天</span></b><b><span style=\"font-size:12pt;\">2</span></b><b><span style=\"font-family:宋体;font-size:12pt;\">分，共</span></b><b><span style=\"font-size:12pt;\">60</span></b><b><span style=\"font-family:宋体;font-size:12pt;\">分）</span></b><b><span style=\"font-size:12pt;\"></span></b> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>1</span><span style=\"font-family:宋体;\">．</span><span> STUDENT_GRADES</span><span style=\"font-family:宋体;\">表结构如下</span><span>( )</span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>STUDENT_ID<span>&nbsp; </span>NUMBER(12)</span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>SEMESTER_END<span>&nbsp; </span>DATE</span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>GPA<span>&nbsp;<br />\n</span>NUMBER(4,3)</span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span style=\"font-family:宋体;\">要求显示</span><span>STUDENT_ID</span><span style=\"font-family:宋体;\">，</span><span>SEMESTER_END</span><span style=\"font-family:宋体;\">，</span><span>GPA</span><span style=\"font-family:宋体;\">，其中首先以</span><span>semester_end</span><span style=\"font-family:宋体;\">从小到大，之后</span><span>GPA</span><span style=\"font-family:宋体;\">从大到小排序显示？</span><span></span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>A</span><span style=\"font-family:宋体;\">．</span><span> SELECT student_id, semester_end, gpaFROM student_gradesORDER BY<br />\nsemester_end DESC, gpa DESC</span><span style=\"font-family:宋体;\">；</span><span></span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>B</span><span style=\"font-family:宋体;\">．</span><span> SELECT student_id, semester_end, gpaFROM student_gradesORDER BY<br />\nsemester_end ASC, gpa ASC</span><span style=\"font-family:宋体;\">；</span><span></span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>C</span><span style=\"font-family:宋体;\">．</span><span> SELECT student_id, semester_end, gpaFROM student_gradesORDER BY<br />\nsemester_end, gpa DESC</span><span style=\"font-family:宋体;\">；</span><span></span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>D</span><span style=\"font-family:宋体;\">．</span><span> SELECT student_id, semester_end, gpaFROM student_gradesORDER BY gpa<br />\nDESC, semester_end DESC</span><span style=\"font-family:宋体;\">；</span><span></span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>E</span><span style=\"font-family:宋体;\">．</span><span> SELECT student_id, semester_end, gpaFROM student_gradesORDER BY gpa<br />\nDESC, semester_end ASC</span><span style=\"font-family:宋体;\">；</span><span></span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>2</span><span style=\"font-family:宋体;\">．</span><br />\n<span style=\"font-family:宋体;\">数据库中有两张表，</span><span>employees(</span><span style=\"font-family:宋体;\">雇员表</span><span>)</span><span style=\"font-family:宋体;\">，</span><span>departments(</span><span style=\"font-family:宋体;\">部门表</span><span>)</span><span style=\"font-family:宋体;\">，表结构为课程中试验案例</span><span>, </span><span style=\"font-family:宋体;\">如果想要获得全部雇员的信息，不管这个雇员是否被分配了部门，以下答案正确的是</span><span>()</span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span><span>&nbsp;&nbsp;&nbsp;<br />\n</span>A</span><span style=\"font-family:宋体;\">．</span><span> SELECT<br />\nlast_name,department_name FROM employees JOIN departments(+)</span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span><span>&nbsp;&nbsp;&nbsp;<br />\n</span>B</span><span style=\"font-family:宋体;\">．</span><span> SELECT<br />\nlast_name,department_name FROM employees(+) JOIN departments</span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span><span>&nbsp;&nbsp;&nbsp;<br />\n</span>C</span><span style=\"font-family:宋体;\">．</span><span> SELECT<br />\nlast_name, department_name FROM employees e RIGHT JOIN departments d ON<br />\n(e.department_id </span><span style=\"font-family:宋体;\">＝</span><span><br />\nd.department_id)</span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span><span>&nbsp;&nbsp;&nbsp;<br />\n</span>D</span><span style=\"font-family:宋体;\">．</span><span> SELECT<br />\nlast_name, department_name FROM employees e LEFT JOIN departments d ON<br />\n(e.department_id </span><span style=\"font-family:宋体;\">＝</span><span><br />\nd.department_id)</span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>3</span><span style=\"font-family:宋体;\">．</span><br />\n<span style=\"font-family:宋体;\">在</span><span>Oracle</span><span style=\"font-family:宋体;\">数据库中创建一个表，对其主键叙述正确的是（）</span><span></span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>A</span><span style=\"font-family:宋体;\">．</span><br />\n<span style=\"font-family:宋体;\">一个表可以有多个主键</span><span><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span>B</span><span style=\"font-family:宋体;\">．</span><br />\n<span style=\"font-family:宋体;\">一个表的主键只能是一列</span><span></span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>C</span><span style=\"font-family:宋体;\">．</span><br />\n<span style=\"font-family:宋体;\">主键必须唯一且不能为空</span><span><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span>D</span><span style=\"font-family:宋体;\">．</span><br />\n<span style=\"font-family:宋体;\">联合主键的列数据类型必须一致</span><span></span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>4</span><span style=\"font-family:宋体;\">．</span><br />\n<span style=\"font-family:宋体;\">部分匹配查询中有关通配符“</span><span>_</span><span style=\"font-family:宋体;\">”的正确的叙述是</span><span>()</span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>A</span><span style=\"font-family:宋体;\">．</span><br />\n<span style=\"font-family:宋体;\">“</span><span>_</span><span style=\"font-family:宋体;\">”代表多个字符</span><span><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span>B</span><span style=\"font-family:宋体;\">．</span> <span style=\"font-family:宋体;\">“</span><span>_</span><span style=\"font-family:宋体;\">”代表一个字符</span> <span></span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>C</span><span style=\"font-family:宋体;\">．</span><br />\n<span style=\"font-family:宋体;\">“</span><span>_</span><span style=\"font-family:宋体;\">”不能与“</span><span>%</span><span style=\"font-family:宋体;\">”一同使用</span><span><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span>D</span><span style=\"font-family:宋体;\">．</span><br />\n<span style=\"font-family:宋体;\">“</span><span>_</span><span style=\"font-family:宋体;\">”代表代表零个或多个字符</span><span></span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>5</span><span style=\"font-family:宋体;\">．</span><br />\n<span style=\"font-family:宋体;\">下面哪条命令能够保证只删除表</span><span>employee.</span><span style=\"font-family:宋体;\">的数据而保留表的结构并且必要是可以回滚</span><span>?()</span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>A</span><span style=\"font-family:宋体;\">．</span><span> DROP TABLE employee.<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br />\n</span>B</span><span style=\"font-family:宋体;\">．</span><span> DELETE FROM<br />\nemployee.</span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>C</span><span style=\"font-family:宋体;\">．</span><span> TRUNCATE TABLE employee.<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br />\n</span>D</span><span style=\"font-family:宋体;\">．</span><span> This task<br />\ncan</span><span style=\"font-family:宋体;\">’</span><span>t be<br />\naccomplished.</span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>6</span><span style=\"font-family:宋体;\">．</span><br />\n<span style=\"font-family:宋体;\">在关系数据库系统中，为了简化用户的查询操作，而又不增加数据的存储空间，常用的方法是创建（）</span><span></span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>A</span><span style=\"font-family:宋体;\">．</span><br />\n<span style=\"font-family:宋体;\">另一个表（</span><span>TABLE</span><span style=\"font-family:宋体;\">）</span><span> B</span><span style=\"font-family:宋体;\">．</span><br />\n<span style=\"font-family:宋体;\">游标（</span><span>CURSOR</span><span style=\"font-family:宋体;\">）</span><span>C</span><span style=\"font-family:宋体;\">．</span><br />\n<span style=\"font-family:宋体;\">视图（</span><span>VIEW</span><span style=\"font-family:宋体;\">）</span><span> D</span><span style=\"font-family:宋体;\">．</span><br />\n<span style=\"font-family:宋体;\">索引（</span><span>INDEX</span><span style=\"font-family:宋体;\">）</span><span></span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>7</span><span style=\"font-family:宋体;\">．</span><br />\n<span style=\"font-family:宋体;\">关于</span><span>TRUNCATE</span><span style=\"font-family:宋体;\">和</span><span>DELETE</span><span style=\"font-family:宋体;\">命令，下列描述正确的是</span><span>(</span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>A</span><span style=\"font-family:宋体;\">．</span><br />\n<span style=\"font-family:宋体;\">两者都属于</span><span>DML</span><span style=\"font-family:宋体;\">语句。</span><span><span>&nbsp;<br />\n</span>B</span><span style=\"font-family:宋体;\">．</span> <span style=\"font-family:宋体;\">两者都不属于</span><span>DML</span><span style=\"font-family:宋体;\">语句。</span><span></span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>C</span><span style=\"font-family:宋体;\">．</span><br />\n<span style=\"font-family:宋体;\">执行</span><span>TRUNCATE</span><span style=\"font-family:宋体;\">命令之后，该会话中先前未提交的事务自动提交。</span><span></span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>D</span><span style=\"font-family:宋体;\">．</span><br />\n<span style=\"font-family:宋体;\">执行</span><span>TRUNCATE</span><span style=\"font-family:宋体;\">命令之后，该会话中先前未提交的事务自动回滚。</span><span></span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>8</span><span style=\"font-family:宋体;\">．</span><br />\n<span style=\"font-family:宋体;\">哪个选项能够限制组函数返回的值</span><span>()</span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>A</span><span style=\"font-family:宋体;\">．</span><span> WHERE<span>&nbsp;&nbsp;&nbsp;&nbsp; </span>B</span><span style=\"font-family:宋体;\">．</span><span> HAVING<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span>C</span><span style=\"font-family:宋体;\">．</span><span> ORDER BY<span>&nbsp;&nbsp;&nbsp; </span>D</span><span style=\"font-family:宋体;\">．</span> <span style=\"font-family:宋体;\">不能限制组函数</span><span></span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>9</span><span style=\"font-family:宋体;\">．</span><br />\n<span style=\"font-family:宋体;\">关于事务的描述，下列说法正确的是</span><span>()</span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>A</span><span style=\"font-family:宋体;\">．</span><br />\n<span style=\"font-family:宋体;\">执行一个</span><span>DDL</span><span style=\"font-family:宋体;\">或</span><span>DCL</span><span style=\"font-family:宋体;\">语句后，事务自动开始。</span><span> </span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>B</span><span style=\"font-family:宋体;\">．</span><br />\n<span style=\"font-family:宋体;\">执行一个</span><span>DDL</span><span style=\"font-family:宋体;\">或</span><span>DCL</span><span style=\"font-family:宋体;\">语句后，事务自动提交。</span><span></span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>C</span><span style=\"font-family:宋体;\">．</span><br />\n<span style=\"font-family:宋体;\">执行一个</span><span>DDL</span><span style=\"font-family:宋体;\">或</span><span>DCL</span><span style=\"font-family:宋体;\">语句后，事务自动回滚。</span><span></span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>D</span><span style=\"font-family:宋体;\">．</span><br />\n<span style=\"font-family:宋体;\">执行一个</span><span>DDL</span><span style=\"font-family:宋体;\">或</span><span>DCL</span><span style=\"font-family:宋体;\">语句后，对当前事务没有影响。</span><span></span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>10</span><span style=\"font-family:宋体;\">．</span><span> Select</span><span style=\"font-family:宋体;\">语句的执行顺序是</span><span>()</span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>(1)select (2)from (3)where (4)group by<br />\n(5)having (6)order by</span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>A</span><span style=\"font-family:宋体;\">．</span><span> 123456<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span>B</span><span style=\"font-family:宋体;\">．</span><span> 234561<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span>C</span><span style=\"font-family:宋体;\">．</span><span> 234516<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span>D</span><span style=\"font-family:宋体;\">．</span><span> 124563</span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>11</span><span style=\"font-family:宋体;\">．</span><br />\n<span style=\"font-family:宋体;\">哪个活动将导致自动回滚（）</span><span></span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>A</span><span style=\"font-family:宋体;\">．</span><span> GRANT<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span>B</span><span style=\"font-family:宋体;\">．</span><span> CREATE<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span>C</span><span style=\"font-family:宋体;\">．</span><br />\n<span style=\"font-family:宋体;\">系统崩溃</span><span><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span>D</span><span style=\"font-family:宋体;\">．</span><br />\n<span style=\"font-family:宋体;\">从</span><span>SQL*Plus</span><span style=\"font-family:宋体;\">退出</span><span></span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>12</span><span style=\"font-family:宋体;\">．</span><br />\n<span style=\"font-family:宋体;\">关于</span><span>Listener</span><span style=\"font-family:宋体;\">的描述正确的是（</span><span><span>&nbsp;&nbsp; </span></span><span style=\"font-family:宋体;\">）</span><span></span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>A</span><span style=\"font-family:宋体;\">．</span><br />\n<span style=\"font-family:宋体;\">监听、接收客户端连接请求</span><span><span>&nbsp;&nbsp; </span>B</span><span style=\"font-family:宋体;\">．</span><br />\n<span style=\"font-family:宋体;\">防止黑客入侵</span> <span><span>&nbsp;&nbsp;</span>C</span><span style=\"font-family:宋体;\">．</span><br />\n<span style=\"font-family:宋体;\">验证连接数据库用户的身份。</span><span></span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>D</span><span style=\"font-family:宋体;\">．</span><br />\n<span style=\"font-family:宋体;\">实现客户端与数据库之间的信息传输</span><span></span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>13</span><span style=\"font-family:宋体;\">．</span><br />\n<span style=\"font-family:宋体;\">查询语句</span><span>SELECT ROUND(13.57)</span><span style=\"font-family:宋体;\">，</span><span>ROUND</span><span style=\"font-family:宋体;\">（</span><span>13.57</span><span style=\"font-family:宋体;\">，</span><span>1</span><span style=\"font-family:宋体;\">），</span><span>ROUND</span><span style=\"font-family:宋体;\">（</span><span>13.57</span><span style=\"font-family:宋体;\">，</span><span>-1</span><span style=\"font-family:宋体;\">），</span><span>TRUNC</span><span style=\"font-family:宋体;\">（</span><span>13.57</span><span style=\"font-family:宋体;\">，</span><span>-2</span><span style=\"font-family:宋体;\">）</span><span> FROM DUAL </span><span style=\"font-family:宋体;\">返回结果，正确的是</span><span>()</span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>A</span><span style=\"font-family:宋体;\">．</span><span>13.57</span><span style=\"font-family:宋体;\">，</span><span>13.6</span><span style=\"font-family:宋体;\">，</span><span>10</span><span style=\"font-family:宋体;\">，</span><span>13<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span>B</span><span style=\"font-family:宋体;\">．</span><span>13</span><span style=\"font-family:宋体;\">，</span><span>13.5</span><span style=\"font-family:宋体;\">，</span><span>14</span><span style=\"font-family:宋体;\">，</span><span>0<span>&nbsp;<br />\n</span></span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>C</span><span style=\"font-family:宋体;\">．</span><span>14</span><span style=\"font-family:宋体;\">，</span><span>13.6</span><span style=\"font-family:宋体;\">，</span><span>10</span><span style=\"font-family:宋体;\">，</span><span>0<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span>D</span><span style=\"font-family:宋体;\">．</span><span>13.6</span><span style=\"font-family:宋体;\">，</span><span>13.5</span><span style=\"font-family:宋体;\">，</span><span>11</span><span style=\"font-family:宋体;\">，</span><span>13</span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>14</span><span style=\"font-family:宋体;\">．</span><br />\n<span style=\"font-family:宋体;\">关于</span><span>ROWNUM</span><span style=\"font-family:宋体;\">的描述，下列正确的是</span><span>()</span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>A</span><span style=\"font-family:宋体;\">．</span><span> ROWNUM</span><span style=\"font-family:宋体;\">是每个表中实际存在的列。</span><span></span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>B</span><span style=\"font-family:宋体;\">．</span><span> ROWNUM</span><span style=\"font-family:宋体;\">是每条查询语句执行完成之后，给每行数据填充的行号，行号从</span><span>0</span><span style=\"font-family:宋体;\">开始。</span><span></span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>C</span><span style=\"font-family:宋体;\">．</span><span> ROWNUM</span><span style=\"font-family:宋体;\">做为条件表达式时，可以使用任何算术运算符。</span><span></span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>D</span><span style=\"font-family:宋体;\">．</span><span> ROWNUM</span><span style=\"font-family:宋体;\">经常用来做为分页查询。</span><span></span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>15</span><span style=\"font-family:宋体;\">．</span><br />\n<span style=\"font-family:宋体;\">当</span><span>quantity</span><span style=\"font-family:宋体;\">值是</span><span>null</span><span style=\"font-family:宋体;\">时，哪个语句显示</span><span>0()</span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>A</span><span style=\"font-family:宋体;\">．</span><span> Select 100/quantity from inventory<span>&nbsp;&nbsp;<br />\n</span>B</span><span style=\"font-family:宋体;\">．</span><span> B. Select<br />\nNVL(100/quantity,0) from inventory</span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>C</span><span style=\"font-family:宋体;\">．</span><span> C. Select NULL(100/quantity,0) from inventory </span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>D</span><span style=\"font-family:宋体;\">．</span><span> D. Select TO_CHAR(100/quantity,0) from inventory</span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>16</span><span style=\"font-family:宋体;\">．</span><br />\n<span style=\"font-family:宋体;\">语句</span><span> WHERE<span>&nbsp; </span>SAL<span>&nbsp;<br />\n</span>BETWEEN 1000 AND 4000 </span><span style=\"font-family:宋体;\">与下列哪个子句等价</span><span>()</span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>A</span><span style=\"font-family:宋体;\">．</span><span>WHERE<span>&nbsp; </span>SAL</span><span style=\"font-family:宋体;\">〉＝</span><span>1000 AND SAL</span><span style=\"font-family:宋体;\">﹤＝</span><span>4000<span>&nbsp; </span>B</span><span style=\"font-family:宋体;\">．</span><span>WHERE<span>&nbsp; </span>SAL</span><span style=\"font-family:宋体;\">〉</span><span>1000 AND SAL</span><span style=\"font-family:宋体;\">﹤</span><span>4000</span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>C</span><span style=\"font-family:宋体;\">．</span><span>WHERE<span>&nbsp; </span>SAL</span><span style=\"font-family:宋体;\">〉＝</span><span>1000 OR SAL</span><span style=\"font-family:宋体;\">﹤＝</span><span>4000<span>&nbsp;&nbsp; </span>D</span><span style=\"font-family:宋体;\">．</span><span>WHERE<span>&nbsp; </span>SAL</span><span style=\"font-family:宋体;\">〉＝</span><span>1000 AND SAL </span><span style=\"font-family:宋体;\">﹤</span><span>4000</span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>17</span><span style=\"font-family:宋体;\">．</span><br />\n<span style=\"font-family:宋体;\">下列约束哪个是只能在列级定义的</span><span>?()</span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>A</span><span style=\"font-family:宋体;\">．</span><span> UNIQUE<span>&nbsp;&nbsp;&nbsp; </span>B</span><span style=\"font-family:宋体;\">．</span><span> NOT NULL<span>&nbsp;&nbsp;&nbsp;&nbsp; </span>C</span><span style=\"font-family:宋体;\">．</span><span> CHECK<span>&nbsp;&nbsp; </span>D</span><span style=\"font-family:宋体;\">．</span><span> PRIMARY KEY E</span><span style=\"font-family:宋体;\">．</span><span> FOREIGN KEY</span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>18</span><span style=\"font-family:宋体;\">．</span><br />\n<span style=\"font-family:宋体;\">在</span><span> STUDENTS</span><span style=\"font-family:宋体;\">表添加主键以下哪个语句是正确的</span><span>?()</span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span><span>&nbsp;&nbsp;&nbsp;<br />\n</span>A</span><span style=\"font-family:宋体;\">．</span><span> ALTER TABLE<br />\nstudents ADD PRIMARY KEY student_id</span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span><span>&nbsp;&nbsp;&nbsp;<br />\n</span>B</span><span style=\"font-family:宋体;\">．</span><span> ALTER TABLE<br />\nstudents ADD CONSTRAINT PRIMARY KEY (student_id)</span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span><span>&nbsp;&nbsp;&nbsp;<br />\n</span>C</span><span style=\"font-family:宋体;\">．</span><span> ALTER TABLE<br />\nstudents ADD CONSTRAINT stud_id_pk PRIMARY KEY student_id</span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span><span>&nbsp;&nbsp;&nbsp;<br />\n</span>D</span><span style=\"font-family:宋体;\">．</span><span> ALTER TABLE<br />\nstudents ADD CONSTRAINT stud_id_pk PRIMARY KEY (student_id)</span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span><span>&nbsp;&nbsp;&nbsp;<br />\n</span>E</span><span style=\"font-family:宋体;\">．</span><span> ALTER TABLE<br />\nstudents MODIFY CONSTRAINT stud_id_pk PRIMARY KEY (student_id)</span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>19</span><span style=\"font-family:宋体;\">．</span><span> SQL</span><span style=\"font-family:宋体;\">语句中连接字符串的符号是（</span><span><span>&nbsp; </span></span><span style=\"font-family:宋体;\">）</span><span></span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>A</span><span style=\"font-family:宋体;\">．</span><br />\n<span style=\"font-family:宋体;\">“</span><span>+</span><span style=\"font-family:宋体;\">”</span><span><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span>B</span><span style=\"font-family:宋体;\">．</span><span><span>&nbsp;<br />\n</span></span><span style=\"font-family:宋体;\">“</span><span>||</span><span style=\"font-family:宋体;\">”</span><span><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span>C</span><span style=\"font-family:宋体;\">．</span><span><span>&nbsp; </span></span><span style=\"font-family:宋体;\">“</span><span>|</span><span style=\"font-family:宋体;\">”</span><span><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span>D</span><span style=\"font-family:宋体;\">．</span> <span style=\"font-family:宋体;\">“</span><span>&amp;</span><span style=\"font-family:宋体;\">”</span><span></span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>20</span><span style=\"font-family:宋体;\">．</span><br />\n<span style=\"font-family:宋体;\">试图</span><span>ANN_SAL</span><span style=\"font-family:宋体;\">是基于</span><span>employee</span><span style=\"font-family:宋体;\">表的，试图的结构如下：</span><span>()</span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>NAME<span>&nbsp;<br />\n</span>NULL<span>&nbsp; </span>TYPE</span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>EMPNO<span>&nbsp;<br />\n</span>NOT NULL<span>&nbsp; </span>NUMBER(4)</span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>YEARLY_SAL<span>&nbsp;&nbsp; </span>NUMBER(9,2)</span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>MONTHLY_SAL<span>&nbsp;&nbsp; </span>NUMBER(9,2)</span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span style=\"font-family:宋体;\">下面哪条语句能够从试图</span><span>ANN_SAL</span><span style=\"font-family:宋体;\">中检索出数据</span><span></span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>A</span><span style=\"font-family:宋体;\">．</span><span> SELECT * FROM ANN_SAL<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br />\n</span>B</span><span style=\"font-family:宋体;\">．</span><span> SELECT *<br />\nFROM EMPLOYEE</span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>C</span><span style=\"font-family:宋体;\">．</span><span> SELECT * FROM VIEW ANN_SAL<span>&nbsp; </span></span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>D</span><span style=\"font-family:宋体;\">．</span><span> SELECT * FROM VIEW ANN_SAL IS DON EMPLOYEE</span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>21</span><span style=\"font-family:宋体;\">．</span><br />\n<span style=\"font-family:宋体;\">对于权限的授予，下列说法正确的是</span><span>()</span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span><span>&nbsp;&nbsp;&nbsp;<br />\n</span>A</span><span style=\"font-family:宋体;\">．</span><span> WITH GRANT<br />\nOPTION </span><span style=\"font-family:宋体;\">选项可以实现系统权限的传递。</span><span></span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span><span>&nbsp;&nbsp;&nbsp;<br />\n</span>B</span><span style=\"font-family:宋体;\">．</span><span> WITH ADMIN<br />\nOPTION </span><span style=\"font-family:宋体;\">选项可以实现对象权限的传递。</span><span></span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span><span>&nbsp;&nbsp;&nbsp;<br />\n</span>C</span><span style=\"font-family:宋体;\">．</span> <span style=\"font-family:宋体;\">以上两种说法都正确。</span><span><span>&nbsp;&nbsp;&nbsp;&nbsp; </span>D</span><span style=\"font-family:宋体;\">．</span> <span style=\"font-family:宋体;\">以上两种说法都不正确。</span><span></span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>22</span><span style=\"font-family:宋体;\">．</span><br />\n<span style=\"font-family:宋体;\">关于对空值的描述，下列说法正确的是</span><span>()</span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span><span>&nbsp;&nbsp;&nbsp;<br />\n</span>A</span><span style=\"font-family:宋体;\">．</span> <span style=\"font-family:宋体;\">包含空值的任何算术表达式结果都等于空。</span><span></span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span><span>&nbsp;&nbsp;&nbsp;<br />\n</span>B</span><span style=\"font-family:宋体;\">．</span> <span style=\"font-family:宋体;\">包含空值的连接字符串</span><span>|| </span><span style=\"font-family:宋体;\">等于与空字符串连接，结果还是空值。</span><span></span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span><span>&nbsp;&nbsp;&nbsp;<br />\n</span>C</span><span style=\"font-family:宋体;\">．</span> <span style=\"font-family:宋体;\">空值做为判断条件是写法是</span><br />\n<span style=\"font-family:宋体;\">：判断字段＝</span><span>null</span><span style=\"font-family:宋体;\">。</span><span></span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span><span>&nbsp;&nbsp;&nbsp;<br />\n</span>D</span><span style=\"font-family:宋体;\">．</span> <span style=\"font-family:宋体;\">表</span><span>Emp</span><span style=\"font-family:宋体;\">中有</span><span>5</span><span style=\"font-family:宋体;\">条记录，</span><span>empID</span><span style=\"font-family:宋体;\">分别为</span><span>1</span><span style=\"font-family:宋体;\">，</span><span>2</span><span style=\"font-family:宋体;\">，</span><span>3</span><span style=\"font-family:宋体;\">，</span><span>4</span><span style=\"font-family:宋体;\">，</span><span>5</span><span style=\"font-family:宋体;\">，那么查询语句</span><span>Select<br />\n* from Emp where empID not in </span><span style=\"font-family:宋体;\">（</span><span>2</span><span style=\"font-family:宋体;\">，</span><span>3</span><span style=\"font-family:宋体;\">，</span><span>4</span><span style=\"font-family:宋体;\">，</span><span>null</span><span style=\"font-family:宋体;\">）的执行后结果是</span><span>2</span><span style=\"font-family:宋体;\">条记录。</span><span></span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>23</span><span style=\"font-family:宋体;\">．</span><br />\n<span style=\"font-family:宋体;\">为了进行数据库访问</span><span>,</span><span style=\"font-family:宋体;\">需要哪个权限</span><span>()</span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>A</span><span style=\"font-family:宋体;\">．</span><span> ALTER USER<span>&nbsp;&nbsp;&nbsp; </span>B</span><span style=\"font-family:宋体;\">．</span><span> CREATE USER<span>&nbsp; </span>C</span><span style=\"font-family:宋体;\">．</span><span> ALTER SYSTEM<span>&nbsp; </span>D</span><span style=\"font-family:宋体;\">．</span><span> CREATE SESSION</span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>24</span><span style=\"font-family:宋体;\">．</span><br />\n<span style=\"font-family:宋体;\">创建表时，一个</span><span>VARCHAR2</span><span style=\"font-family:宋体;\">的列没有指定长度，它的缺省长度是多少？</span><span>()</span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span><span>&nbsp;</span>A</span><span style=\"font-family:宋体;\">．</span><span> 1<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span>B</span><span style=\"font-family:宋体;\">．</span><span> 25<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span>C</span><span style=\"font-family:宋体;\">．</span><span> 4000<span>&nbsp;&nbsp;&nbsp;&nbsp; </span>D</span><span style=\"font-family:宋体;\">．</span><br />\n<span style=\"font-family:宋体;\">对于</span><span>VARCHAR2</span><span style=\"font-family:宋体;\">列，必须指定列的长度</span><span></span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>25</span><span style=\"font-family:宋体;\">．</span><br />\n<span style=\"font-family:宋体;\">将工资低于</span><span>3000</span><span style=\"font-family:宋体;\">元或者低于平均工资的员工的工资上浮</span><span>10%</span><span style=\"font-family:宋体;\">，下面哪条语句正确（</span><span><span>&nbsp; </span></span><span style=\"font-family:宋体;\">）</span><span></span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>A</span><span style=\"font-family:宋体;\">．</span><span> UPDATE EMP SET SAL=SAL*1.1 WHERE SAL </span><span style=\"font-family:宋体;\">﹤</span><span>3000 OR SAL</span><span style=\"font-family:宋体;\">﹤</span><span>AVG(SAL)</span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>B</span><span style=\"font-family:宋体;\">．</span><span> UPDATE SET SAL=SAL*1.1 FROM EMP WHERE SAL </span><span style=\"font-family:宋体;\">﹤</span><span>3000 OR SAL</span><span style=\"font-family:宋体;\">﹤</span><span>AVG(SAL)</span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>C</span><span style=\"font-family:宋体;\">．</span><span> UPDATE SET SAL=SAL*1.1 FROM EMP WHERE SAL</span><span style=\"font-family:宋体;\">﹤</span><span>3000 OR SAL</span><span style=\"font-family:宋体;\">﹤</span><span>(SELECT AVG(SAL) FROM EMP)</span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>D</span><span style=\"font-family:宋体;\">．</span><span> UPDATE EMP SET SAL=SAL*1.1 WHERE SAL</span><span style=\"font-family:宋体;\">﹤</span><span>3000 OR SAL</span><span style=\"font-family:宋体;\">﹤</span><span>(SELECT AVG(SAL) FROM EMP)</span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>26</span><span style=\"font-family:宋体;\">．</span><span> MARKS</span><span style=\"font-family:宋体;\">表结构如下</span><span>()</span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>STD_ID<span>&nbsp;<br />\n</span>NUMBER(4)</span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>STUDENT_NAME<span>&nbsp; </span>VARCHAR2(30)</span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>SUBJ1<span>&nbsp;<br />\n</span>NUMBER(3)</span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>SUBJ2<span>&nbsp;<br />\n</span>NUMBER(3)</span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span style=\"font-family:宋体;\">下面</span><span>sql</span><span style=\"font-family:宋体;\">语句中是否存在错误</span><span></span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>SELECT subj1+subj2 total_marks, std_id</span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>FROM marks</span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>WHERE subj1 &gt; AVG(subj1) AND subj2<br />\n&gt; AVG(subj2)</span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>ORDER BY total_marks;</span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span style=\"font-family:宋体;\">如果有错误，请指出是哪一项？</span><span></span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>A</span><span style=\"font-family:宋体;\">．</span><br />\n<span style=\"font-family:宋体;\">能够成功执行</span><span><span>&nbsp;&nbsp;&nbsp; </span><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span>B</span><span style=\"font-family:宋体;\">．</span><span> SELECT</span><span style=\"font-family:宋体;\">子句中有错误</span><span></span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>C</span><span style=\"font-family:宋体;\">．</span><span> WHERE</span><span style=\"font-family:宋体;\">子句中有错误</span><span><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span>D</span><span style=\"font-family:宋体;\">．</span><span> ORDER BY</span><span style=\"font-family:宋体;\">子句中有错误</span><span></span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>27</span><span style=\"font-family:宋体;\">．</span><span> EMP</span><span style=\"font-family:宋体;\">表结构如下</span><span>()</span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>ENAME<span>&nbsp;&nbsp;&nbsp;<br />\n</span>VARCHAR2(35)</span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>SALARY<span>&nbsp;&nbsp;&nbsp;<br />\n</span>NUMBER(8,2)</span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>HIRE_DATE<span>&nbsp;&nbsp; </span>DATE</span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span style=\"font-family:宋体;\">下面那句话列出了在公司工作了五年以上的员工？</span><span></span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>A</span><span style=\"font-family:宋体;\">．</span><span> SELECT ENAMEFROM EMPWHERE SYSDATE-HIRE_DATE </span><span style=\"font-family:宋体;\">〉</span><span> 5</span><span style=\"font-family:宋体;\">；</span><span></span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>B</span><span style=\"font-family:宋体;\">．</span><span> SELECT ENAMEFROM EMPWHERE HIRE_DATE-SYSDATE </span><span style=\"font-family:宋体;\">〉</span><span> 5</span><span style=\"font-family:宋体;\">；</span><span></span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>C</span><span style=\"font-family:宋体;\">．</span><span> SELECT ENAMEFROM EMPWHERE (SYSDATE-HIRE_DATE)/365 </span><span style=\"font-family:宋体;\">〉</span><span> 5</span><span style=\"font-family:宋体;\">；</span><span></span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>D</span><span style=\"font-family:宋体;\">．</span><span> SELECT ENAMEFROM EMPWHERE (SYSDATE-HIRE_DATE)* 365 </span><span style=\"font-family:宋体;\">〉</span><span> 5</span><span style=\"font-family:宋体;\">；</span><span></span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>28</span><span style=\"font-family:宋体;\">．</span><br />\n<span style=\"font-family:宋体;\">查询语句</span><span>SELECT SUBSTR(</span><span style=\"font-family:宋体;\">‘</span><span>Hello World</span><span style=\"font-family:宋体;\">’</span><span>,4,5) FROM DUAL;</span><span style=\"font-family:宋体;\">返回结果，正确的是</span><span>()</span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span><span>&nbsp;</span>A</span><span style=\"font-family:宋体;\">．</span><span> loWor<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span>B</span><span style=\"font-family:宋体;\">．</span><span> World<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span>C</span><span style=\"font-family:宋体;\">．</span><span> lloWo<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span>D</span><span style=\"font-family:宋体;\">．</span><span> lo Wo</span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>29</span><span style=\"font-family:宋体;\">．</span><span> EMP</span><span style=\"font-family:宋体;\">表结构如下</span><span>()</span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>LAST NAME<span>&nbsp;<br />\n</span>VARCNAR2(35)<span>&nbsp; </span>NOT NULL</span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>SALARY<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span>NUMBER(9,2)<span>&nbsp; </span>NOT NULL</span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>COMMISION_PCT<span>&nbsp;&nbsp;&nbsp;&nbsp; </span>NUMBER(4,2)</span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span style=\"font-family:宋体;\">要计算所有雇员的</span><span>12*salary*<br />\ncommission_pct</span><span style=\"font-family:宋体;\">值</span><span>,</span><span style=\"font-family:宋体;\">下面哪条语句能够保证显示所有雇员的</span><span>12*salary*<br />\ncommission_pct</span><span style=\"font-family:宋体;\">值</span><span></span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>A</span><span style=\"font-family:宋体;\">．</span><span> SELECT last_name, 12*salary* commission_pctFROM emp</span><span style=\"font-family:宋体;\">；</span><span></span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>B</span><span style=\"font-family:宋体;\">．</span><br />\n<span>SELECT last_name, 12*salary* (commission_pct,0)FROM emp</span><span style=\"font-family:宋体;\">；</span><span></span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>C</span><span style=\"font-family:宋体;\">．</span><span> SELECT last_name, 12*salary*(nvl(commission_pct,0))FROM emp</span><span style=\"font-family:宋体;\">；</span><span></span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>D</span><span style=\"font-family:宋体;\">．</span><span> SELECT last_name, 12*salary*(decode(commission_pct,0))FROM emp</span><span style=\"font-family:宋体;\">；</span><span></span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>30</span><span style=\"font-family:宋体;\">．</span><br />\n<span style=\"font-family:宋体;\">查看序列</span><span>DEPT_DEPTNO</span><span style=\"font-family:宋体;\">的当前值，应该使用伪列</span><span>(<span>&nbsp;&nbsp;&nbsp;&nbsp; </span>)</span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>A</span><span style=\"font-family:宋体;\">．</span><span> CURRVAL<span>&nbsp;&nbsp;&nbsp; </span>B</span><span style=\"font-family:宋体;\">．</span><span> NEXTVAL<span>&nbsp;&nbsp;&nbsp;&nbsp; </span>C</span><span style=\"font-family:宋体;\">．</span><span> MAXVAL<span>&nbsp;&nbsp;&nbsp;&nbsp; </span>D</span><span style=\"font-family:宋体;\">．</span><span> MINVAL</span> \n</p>\n<br />\n<br />\n<p class=\"MsoNormal\">\n	<span></span> \n</p>\n<br />\n<br />\n<p class=\"MsoNormal\">\n	<span></span> \n</p>\n<br />\n<br />\n<p class=\"MsoNormal\">\n	<b><span style=\"font-family:宋体;font-size:12pt;\">四</span></b><b><span style=\"font-size:12pt;\">.</span></b><b><span style=\"font-family:宋体;font-size:12pt;\">编程题（本题共</span></b><b><span style=\"font-size:12pt;\">3</span></b><b><span style=\"font-family:宋体;font-size:12pt;\">道小题，</span></b><b><span style=\"font-size:12pt;\">1</span></b><b><span style=\"font-family:宋体;font-size:12pt;\">题</span></b><b><span style=\"font-size:12pt;\">4</span></b><b><span style=\"font-family:宋体;font-size:12pt;\">分，</span></b><b><span style=\"font-size:12pt;\">2</span></b><b><span style=\"font-family:宋体;font-size:12pt;\">题</span></b><b><span style=\"font-size:12pt;\">6</span></b><b><span style=\"font-family:宋体;font-size:12pt;\">分，</span></b><b><span style=\"font-size:12pt;\">3</span></b><b><span style=\"font-family:宋体;font-size:12pt;\">题</span></b><b><span style=\"font-size:12pt;\">20</span></b><b><span style=\"font-family:宋体;font-size:12pt;\">分，共</span></b><b><span style=\"font-size:12pt;\">30</span></b><b><span style=\"font-family:宋体;font-size:12pt;\">分）</span></b><b><span style=\"font-size:12pt;\"></span></b> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>1</span><span style=\"font-family:宋体;\">．</span><br />\n<span style=\"font-family:宋体;\">设有关系</span><span>EMP</span><span style=\"font-family:宋体;\">（</span><span>ENO</span><span style=\"font-family:宋体;\">，</span><span>ENAME</span><span style=\"font-family:宋体;\">，</span><span>SALARY</span><span style=\"font-family:宋体;\">，</span><span>DNO</span><span style=\"font-family:宋体;\">），其中各属性的含义依次为职工号、姓名、工资和所在部门号，以及关系</span><span>DEPT</span><span style=\"font-family:宋体;\">（</span><span>DNO</span><span style=\"font-family:宋体;\">，</span><span>DNAME</span><span style=\"font-family:宋体;\">，</span><span>MANAGER</span><span style=\"font-family:宋体;\">），其中各属性含义依次为部门号、部门名称、部门经理的职工号。</span><span></span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span style=\"font-family:宋体;\">试用</span><span>SQL</span><span style=\"font-family:宋体;\">语句完成以下查询：</span><span> </span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span style=\"font-family:宋体;\">　　列出各部门中工资不低于</span><span>600</span><span style=\"font-family:宋体;\">元的职工的平均工资。</span><span></span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span><span>&nbsp;&nbsp; </span></span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>2</span><span style=\"font-family:宋体;\">．</span><br />\n<span style=\"font-family:宋体;\">设有关系</span><span>EMP</span><span style=\"font-family:宋体;\">（</span><span>ENO</span><span style=\"font-family:宋体;\">，</span><span>ENAME</span><span style=\"font-family:宋体;\">，</span><span>SALARY</span><span style=\"font-family:宋体;\">，</span><span>DNO</span><span style=\"font-family:宋体;\">），其中各属性的含义依次为职工号、姓名、工资和所在部门号，以及关系</span><span>DEPT</span><span style=\"font-family:宋体;\">（</span><span>DNO</span><span style=\"font-family:宋体;\">，</span><span>DNAME</span><span style=\"font-family:宋体;\">，</span><span>MANAGER</span><span style=\"font-family:宋体;\">），其中各属性含义依次为部门号、部门名称、部门经理的职工号。</span><span></span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span style=\"font-family:宋体;\">请用</span><span>SQL</span><span style=\"font-family:宋体;\">语句将“销售部”的那些工资数额低于</span><span>600</span><span style=\"font-family:宋体;\">的职工的工资上调</span><span>10%</span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span style=\"color:navy;font-family:宋体;font-size:9pt;\"><span>&nbsp; </span></span><span>3</span><span style=\"font-family:宋体;\">．</span> <span style=\"font-family:宋体;\">现要为学校建立一个数据库，设学校环境如下：一个系有若干个专业，每个专业一年只招一个班，每个班有若干个学生。</span><span>.</span><span style=\"font-family:宋体;\">现要建立关于学生、班级、系的数据库，关系模式为：</span><span></span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>STUDENT </span><span style=\"font-family:宋体;\">（</span><span>stu_id(</span><span style=\"font-family:宋体;\">学号</span><span>)</span><span style=\"font-family:宋体;\">，</span><span> name(</span><span style=\"font-family:宋体;\">姓名</span><span>)</span><span style=\"font-family:宋体;\">，</span><span>age(</span><span style=\"font-family:宋体;\">年龄</span><span>)</span><span style=\"font-family:宋体;\">，</span><span>class_id(</span><span style=\"font-family:宋体;\">班号</span><span>)</span><span style=\"font-family:宋体;\">）</span><span></span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>CLASS </span><span style=\"font-family:宋体;\">（</span><span>class_id(</span><span style=\"font-family:宋体;\">班号</span><span>)</span><span style=\"font-family:宋体;\">，</span><span>spe_name(</span><span style=\"font-family:宋体;\">专业名</span><span>)</span><span style=\"font-family:宋体;\">，</span><span>dept_id(</span><span style=\"font-family:宋体;\">系号</span><span>)</span><span style=\"font-family:宋体;\">，</span><span>stu_date(</span><span style=\"font-family:宋体;\">入学年份</span><span>)</span><span style=\"font-family:宋体;\">）</span><span></span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span>DEPARTMENT </span><span style=\"font-family:宋体;\">（</span><span>dept_id(</span><span style=\"font-family:宋体;\">系号</span><span>)</span><span style=\"font-family:宋体;\">，</span><span>dept_name(</span><span style=\"font-family:宋体;\">系名</span><span>)</span><span style=\"font-family:宋体;\">）</span><span></span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span style=\"font-family:宋体;\">试用</span><span>SQL</span><span style=\"font-family:宋体;\">语言完成以下功能：</span><span></span> \n</p>\n<br />\n<br />\n<p style=\"text-indent:21pt;\" class=\"MsoBodyText\">\n	<span>1.</span><span style=\"font-family:宋体;\">建表，在定义中要求声明：</span><span></span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span><span>&nbsp; </span></span><span style=\"font-family:宋体;\">（</span><span>1</span><span style=\"font-family:宋体;\">）每个表的主外键。其中同名列</span><span></span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span><span>&nbsp; </span></span><span style=\"font-family:宋体;\">（</span><span>2</span><span style=\"font-family:宋体;\">）学生的年龄介于</span><span>15</span><span style=\"font-family:宋体;\">到</span><span>40</span><span style=\"font-family:宋体;\">岁之间。</span><span></span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span><span>&nbsp; </span></span><span style=\"font-family:宋体;\">（</span><span>3</span><span style=\"font-family:宋体;\">）学生姓名不能为空。</span><span></span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span><span>&nbsp; </span></span><span style=\"font-family:宋体;\">（</span><span>4</span><span style=\"font-family:宋体;\">）只有班里没有学生的时候，才能删除该班级。</span><span></span> \n</p>\n<br />\n<br />\n<p style=\"text-indent:21pt;\" class=\"MsoBodyText\">\n	<span>2. </span><span style=\"font-family:宋体;\">插入如下数据</span><span></span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span><span>&nbsp;&nbsp;<br />\n</span>CLASS</span><span style=\"font-family:宋体;\">（</span><span></span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span><span>&nbsp;&nbsp;&nbsp;<br />\n</span>101</span><span style=\"font-family:宋体;\">，软件，</span><span>002</span><span style=\"font-family:宋体;\">，</span><span>1995</span><span style=\"font-family:宋体;\">；</span><span><span>&nbsp;&nbsp;&nbsp; </span>102</span><span style=\"font-family:宋体;\">，微电子，</span><span>002</span><span style=\"font-family:宋体;\">，</span><span>1996</span><span style=\"font-family:宋体;\">；）</span><span></span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span><span>&nbsp;&nbsp;<br />\n</span>STUDENT </span><span style=\"font-family:宋体;\">（</span><span></span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span><span>&nbsp;&nbsp;&nbsp;<br />\n</span>8101</span><span style=\"font-family:宋体;\">，张三，</span><span>18</span><span style=\"font-family:宋体;\">，</span><span>101</span><span style=\"font-family:宋体;\">；</span><span><span>&nbsp;&nbsp;&nbsp; </span>8102</span><span style=\"font-family:宋体;\">，钱四，</span><span>16</span><span style=\"font-family:宋体;\">，</span><span>121</span><span style=\"font-family:宋体;\">；</span> <span style=\"font-family:宋体;\">）</span><span></span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span><span>&nbsp;&nbsp;<br />\n</span>DEPARTMENT</span><span style=\"font-family:宋体;\">（</span><span> 001</span><span style=\"font-family:宋体;\">，</span><span><span>&nbsp;<br />\n</span></span><span style=\"font-family:宋体;\">数学；</span><span> 002</span><span style=\"font-family:宋体;\">，</span><span><span>&nbsp;<br />\n</span></span><span style=\"font-family:宋体;\">计算机；</span> <span style=\"font-family:宋体;\">）</span><span></span> \n</p>\n<br />\n<br />\n<p style=\"text-indent:21pt;\" class=\"MsoBodyText\">\n	<span>3. </span><span style=\"font-family:宋体;\">完成以下查询功能</span><span></span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span><span>&nbsp;&nbsp; </span></span><span style=\"font-family:宋体;\">（</span><span>1</span><span style=\"font-family:宋体;\">）找出所有姓王的学生，并按其年龄由小到大排序。</span><span></span> \n</p>\n<br />\n<br />\n<p class=\"MsoBodyText\">\n	<span><span>&nbsp;&nbsp; </span></span><span style=\"font-family:宋体;\">（</span><span>2</span><span style=\"font-family:宋体;\">）列出学生人数大于等于</span><span>2</span><span style=\"font-family:宋体;\">的系的编号和名称。</span><span></span> \n</p>\n<br />\n<br />\n<p style=\"text-indent:21pt;\" class=\"MsoBodyText\">\n	<span>4. </span><span style=\"font-family:宋体;\">学校又新增加了一个物理系，编号为</span><span>006</span><span style=\"font-family:宋体;\">。</span><span></span> \n</p>\n<br />\n<br />\n<p style=\"text-indent:21pt;\" class=\"MsoBodyText\">\n	<span>5. </span><span style=\"font-family:宋体;\">对</span><span>STUDENT</span><span style=\"font-family:宋体;\">表新添加了</span><span>email</span><span style=\"font-family:宋体;\">列。</span><span></span> \n</p>\n<br />\n<br />\n<p style=\"text-indent:21pt;\" class=\"MsoBodyText\">\n	<span>6. </span><span style=\"font-family:宋体;\">学生张三转到计算机系微电子班，请更新相关的表。</span><span></span> \n</p>\n<br />\n<br />\n<p style=\"text-align:left;margin-left:10pt;\" class=\"MsoNormal\" align=\"left\">\n	<span style=\"color: rgb(0, 64, 128); font-family: ;\" 微软雅黑?,?sans-serif?;font-size:7.5pt;?=\"\">1.</span> \n</p>\n<br />\n<br />\n<p style=\"text-align:left;margin-left:10pt;\" class=\"MsoNormal\" align=\"left\">\n	<span style=\"color: rgb(0, 64, 128); font-family: ;\" 微软雅黑?,?sans-serif?;font-size:7.5pt;?=\"\">CREATE TABLE department (</span> \n</p>\n<br />\n<br />\n<p style=\"text-align:left;margin-left:10pt;\" class=\"MsoNormal\" align=\"left\">\n	<span style=\"color: rgb(0, 64, 128); font-family: ;\" 微软雅黑?,?sans-serif?;font-size:7.5pt;?=\"\"><span>&nbsp;&nbsp;&nbsp; </span>dept_id NUMBER (8) CONSTRAINT<br />\ndepartment_deptid_pk PRIMARY KEY, </span> \n</p>\n<br />\n<br />\n<p style=\"text-align:left;margin-left:10pt;\" class=\"MsoNormal\" align=\"left\">\n	<span style=\"color: rgb(0, 64, 128); font-family: ;\" 微软雅黑?,?sans-serif?;font-size:7.5pt;?=\"\"><span>&nbsp;&nbsp;&nbsp; </span>dept_name VARCHAR2(20)</span> \n</p>\n<br />\n<br />\n<p style=\"text-align:left;margin-left:10pt;\" class=\"MsoNormal\" align=\"left\">\n	<span style=\"color: rgb(0, 64, 128); font-family: ;\" 微软雅黑?,?sans-serif?;font-size:7.5pt;?=\"\"><span>&nbsp;&nbsp;&nbsp; </span>)</span> \n</p>\n<br />\n<br />\n<p style=\"text-align:left;margin-left:10pt;\" class=\"MsoNormal\" align=\"left\">\n	<span style=\"color: rgb(0, 64, 128); font-family: ;\" 微软雅黑?,?sans-serif?;font-size:7.5pt;?=\"\">CREATE TABLE CLASS (</span> \n</p>\n<br />\n<br />\n<p style=\"text-align:left;margin-left:10pt;\" class=\"MsoNormal\" align=\"left\">\n	<span style=\"color: rgb(0, 64, 128); font-family: ;\" 微软雅黑?,?sans-serif?;font-size:7.5pt;?=\"\"><span>&nbsp;&nbsp;&nbsp; </span>class_id NUMBER (8) CONSTRAINT<br />\nclass_id_pk PRIMARY KEY,</span> \n</p>\n<br />\n<br />\n<p style=\"text-align:left;margin-left:10pt;\" class=\"MsoNormal\" align=\"left\">\n	<span style=\"color: rgb(0, 64, 128); font-family: ;\" 微软雅黑?,?sans-serif?;font-size:7.5pt;?=\"\"><span>&nbsp;&nbsp;&nbsp; </span>spe_name VARCHAR2(20),</span> \n</p>\n<br />\n<br />\n<p style=\"text-align:left;margin-left:10pt;\" class=\"MsoNormal\" align=\"left\">\n	<span style=\"color: rgb(0, 64, 128); font-family: ;\" 微软雅黑?,?sans-serif?;font-size:7.5pt;?=\"\"><span>&nbsp;&nbsp;&nbsp; </span>dept_id NUMBER (8) CONSTRAINT<br />\ndepartment_class_deptid_fk REFERENCES department (dept_id),</span> \n</p>\n<br />\n<br />\n<p style=\"text-align:left;text-indent:14.25pt;margin-left:10pt;\" class=\"MsoNormal\" align=\"left\">\n	<span style=\"color: rgb(0, 64, 128); font-family: ;\" 微软雅黑?,?sans-serif?;font-size:7.5pt;?=\"\">stu_date DATE )</span> \n</p>\n<br />\n<br />\n<p style=\"text-align:left;\" class=\"MsoNormal\" align=\"left\">\n	<span style=\"color: rgb(0, 64, 128); font-family: ;\" 微软雅黑?,?sans-serif?;font-size:7.5pt;?=\"\">CREATE TABLE student (</span> \n</p>\n<br />\n<br />\n<p style=\"text-align:left;margin-left:10pt;\" class=\"MsoNormal\" align=\"left\">\n	<span style=\"color: rgb(0, 64, 128); font-family: ;\" 微软雅黑?,?sans-serif?;font-size:7.5pt;?=\"\"><span>&nbsp;&nbsp; </span>stu_id<span>&nbsp; </span>NUMBER (8),</span> \n</p>\n<br />\n<br />\n<p style=\"text-align:left;margin-left:10pt;\" class=\"MsoNormal\" align=\"left\">\n	<span style=\"color: rgb(0, 64, 128); font-family: ;\" 微软雅黑?,?sans-serif?;font-size:7.5pt;?=\"\"><span>&nbsp;&nbsp; </span>NAME VARCHAR2(20) NOT NULL ,</span> \n</p>\n<br />\n<br />\n<p style=\"text-align:left;margin-left:10pt;\" class=\"MsoNormal\" align=\"left\">\n	<span style=\"color: rgb(0, 64, 128); font-family: ;\" 微软雅黑?,?sans-serif?;font-size:7.5pt;?=\"\"><span>&nbsp;&nbsp; </span>age NUMBER (2) CONSTRAINT<br />\nstudent_ck CHECK (age&gt;=15 AND age &lt;= 40),</span> \n</p>\n<br />\n<br />\n<p style=\"text-align:left;margin-left:10pt;\" class=\"MsoNormal\" align=\"left\">\n	<span style=\"color: rgb(0, 64, 128); font-family: ;\" 微软雅黑?,?sans-serif?;font-size:7.5pt;?=\"\"><span>&nbsp;&nbsp; </span>class_id NUMBER (8) CONSTRAINT<br />\nstudent_class_class_id_fk REFERENCES CLASS(class_id)</span> \n</p>\n<br />\n<br />\n<p style=\"text-align:left;margin-left:10pt;\" class=\"MsoNormal\" align=\"left\">\n	<span style=\"color: rgb(0, 64, 128); font-family: ;\" 微软雅黑?,?sans-serif?;font-size:7.5pt;?=\"\"><span>&nbsp;&nbsp; </span>)</span> \n</p>\n<br />\n<br />\n<p style=\"text-align:left;margin-left:10pt;\" class=\"MsoNormal\" align=\"left\">\n	<span style=\"color: rgb(0, 64, 128); font-family: ;\" 微软雅黑?,?sans-serif?;font-size:7.5pt;?=\"\">2.</span> \n</p>\n<br />\n<br />\n<p style=\"text-align:left;margin-left:10pt;\" class=\"MsoNormal\" align=\"left\">\n	<span style=\"color: rgb(0, 64, 128); font-family: ;\" 微软雅黑?,?sans-serif?;font-size:7.5pt;?=\"\">INSERT INTO department </span> \n</p>\n<br />\n<br />\n<p style=\"text-align:left;margin-left:10pt;\" class=\"MsoNormal\" align=\"left\">\n	<span style=\"color: rgb(0, 64, 128); font-family: ;\" 微软雅黑?,?sans-serif?;font-size:7.5pt;?=\"\">VALUES (001,\'</span><span style=\"color: rgb(0, 64, 128); font-family: ;\" 微软雅黑?,?sans-serif?;font-size:7.5pt;?=\"\">数学</span><span style=\"color: rgb(0, 64, 128); font-family: ;\" 微软雅黑?,?sans-serif?;font-size:7.5pt;?=\"\">\')</span> \n</p>\n<br />\n<br />\n<p style=\"text-align:left;margin-left:10pt;\" class=\"MsoNormal\" align=\"left\">\n	<span style=\"color: rgb(0, 64, 128); font-family: ;\" 微软雅黑?,?sans-serif?;font-size:7.5pt;?=\"\">INSERT INTO department </span> \n</p>\n<br />\n<br />\n<p style=\"text-align:left;margin-left:10pt;\" class=\"MsoNormal\" align=\"left\">\n	<span style=\"color: rgb(0, 64, 128); font-family: ;\" 微软雅黑?,?sans-serif?;font-size:7.5pt;?=\"\">VALUES (002,\'</span><span style=\"color: rgb(0, 64, 128); font-family: ;\" 微软雅黑?,?sans-serif?;font-size:7.5pt;?=\"\">计算机</span><span style=\"color: rgb(0, 64, 128); font-family: ;\" 微软雅黑?,?sans-serif?;font-size:7.5pt;?=\"\">\')</span> \n</p>\n<br />\n<br />\n<p style=\"text-align:left;margin-left:10pt;\" class=\"MsoNormal\" align=\"left\">\n	<span style=\"color: rgb(0, 64, 128); font-family: ;\" 微软雅黑?,?sans-serif?;font-size:7.5pt;?=\"\">INSERT INTO CLASS </span> \n</p>\n<br />\n<br />\n<p style=\"text-align:left;margin-left:10pt;\" class=\"MsoNormal\" align=\"left\">\n	<span style=\"color: rgb(0, 64, 128); font-family: ;\" 微软雅黑?,?sans-serif?;font-size:7.5pt;?=\"\">VALUES (101,\'</span><span style=\"color: rgb(0, 64, 128); font-family: ;\" 微软雅黑?,?sans-serif?;font-size:7.5pt;?=\"\">软件</span><span style=\"color: rgb(0, 64, 128); font-family: ;\" 微软雅黑?,?sans-serif?;font-size:7.5pt;?=\"\">\',002,1995)</span> \n</p>\n<br />\n<br />\n<p style=\"text-align:left;margin-left:10pt;\" class=\"MsoNormal\" align=\"left\">\n	<span style=\"color: rgb(0, 64, 128); font-family: ;\" 微软雅黑?,?sans-serif?;font-size:7.5pt;?=\"\">INSERT INTO CLASS </span> \n</p>\n<br />\n<br />\n<p style=\"text-align:left;margin-left:10pt;\" class=\"MsoNormal\" align=\"left\">\n	<span style=\"color: rgb(0, 64, 128); font-family: ;\" 微软雅黑?,?sans-serif?;font-size:7.5pt;?=\"\">VALUES (102,\'</span><span style=\"color: rgb(0, 64, 128); font-family: ;\" 微软雅黑?,?sans-serif?;font-size:7.5pt;?=\"\">微电子</span><span style=\"color: rgb(0, 64, 128); font-family: ;\" 微软雅黑?,?sans-serif?;font-size:7.5pt;?=\"\">\',002,1996)</span> \n</p>\n<br />\n<br />\n<p style=\"text-align:left;margin-left:10pt;\" class=\"MsoNormal\" align=\"left\">\n	<span style=\"color: rgb(0, 64, 128); font-family: ;\" 微软雅黑?,?sans-serif?;font-size:7.5pt;?=\"\">INSERT INTO student </span> \n</p>\n<br />\n<br />\n<p style=\"text-align:left;margin-left:10pt;\" class=\"MsoNormal\" align=\"left\">\n	<span style=\"color: rgb(0, 64, 128); font-family: ;\" 微软雅黑?,?sans-serif?;font-size:7.5pt;?=\"\">VALUES (8101,\'</span><span style=\"color: rgb(0, 64, 128); font-family: ;\" 微软雅黑?,?sans-serif?;font-size:7.5pt;?=\"\">张三</span><span style=\"color: rgb(0, 64, 128); font-family: ;\" 微软雅黑?,?sans-serif?;font-size:7.5pt;?=\"\">\',18,101)</span> \n</p>\n<br />\n<br />\n<p style=\"text-align:left;margin-left:10pt;\" class=\"MsoNormal\" align=\"left\">\n	<span style=\"color: rgb(0, 64, 128); font-family: ;\" 微软雅黑?,?sans-serif?;font-size:7.5pt;?=\"\">INSERT INTO student </span> \n</p>\n<br />\n<br />\n<p style=\"text-align:left;margin-left:10pt;\" class=\"MsoNormal\" align=\"left\">\n	<span style=\"color: rgb(0, 64, 128); font-family: ;\" 微软雅黑?,?sans-serif?;font-size:7.5pt;?=\"\">VALUES (8102,\'</span><span style=\"color: rgb(0, 64, 128); font-family: ;\" 微软雅黑?,?sans-serif?;font-size:7.5pt;?=\"\">钱四</span><span style=\"color: rgb(0, 64, 128); font-family: ;\" 微软雅黑?,?sans-serif?;font-size:7.5pt;?=\"\">\',16,121)</span> \n</p>\n<br />\n<br />\n<p style=\"text-align:left;margin-left:10pt;\" class=\"MsoNormal\" align=\"left\">\n	<span style=\"color: rgb(0, 64, 128); font-family: ;\" 微软雅黑?,?sans-serif?;font-size:7.5pt;?=\"\">3.</span> \n</p>\n<br />\n<br />\n<p style=\"text-align:left;margin-left:10pt;\" class=\"MsoNormal\" align=\"left\">\n	<span style=\"color: rgb(0, 64, 128); font-family: ;\" 微软雅黑?,?sans-serif?;font-size:7.5pt;?=\"\">SELECT * FROM student WHERE NAME LIKE (\'</span><span style=\"color: rgb(0, 64, 128); font-family: ;\" 微软雅黑?,?sans-serif?;font-size:7.5pt;?=\"\">王</span><span style=\"color: rgb(0, 64, 128); font-family: ;\" 微软雅黑?,?sans-serif?;font-size:7.5pt;?=\"\">%\') ORDER BY age</span> \n</p>\n<br />\n<br />\n<p style=\"text-align:left;margin-left:10pt;\" class=\"MsoNormal\" align=\"left\">\n	<span style=\"color: rgb(0, 64, 128); font-family: ;\" 微软雅黑?,?sans-serif?;font-size:7.5pt;?=\"\">SELECT d.dept_id, d.dept_name FROM department d, CLASS c, student s</span> \n</p>\n<br />\n<br />\n<p style=\"text-align:left;margin-left:10pt;\" class=\"MsoNormal\" align=\"left\">\n	<span style=\"color: rgb(0, 64, 128); font-family: ;\" 微软雅黑?,?sans-serif?;font-size:7.5pt;?=\"\">WHERE d.dept_id = c.dept_id</span> \n</p>\n<br />\n<br />\n<p style=\"text-align:left;margin-left:10pt;\" class=\"MsoNormal\" align=\"left\">\n	<span style=\"color: rgb(0, 64, 128); font-family: ;\" 微软雅黑?,?sans-serif?;font-size:7.5pt;?=\"\">AND c.class_id = s.class_id</span> \n</p>\n<br />\n<br />\n<p style=\"text-align:left;margin-left:10pt;\" class=\"MsoNormal\" align=\"left\">\n	<span style=\"color: rgb(0, 64, 128); font-family: ;\" 微软雅黑?,?sans-serif?;font-size:7.5pt;?=\"\">GROUP BY d.dept_id, d.dept_name</span> \n</p>\n<br />\n<br />\n<p style=\"text-align:left;margin-left:10pt;\" class=\"MsoNormal\" align=\"left\">\n	<span style=\"color: rgb(0, 64, 128); font-family: ;\" 微软雅黑?,?sans-serif?;font-size:7.5pt;?=\"\">HAVING COUNT(1)&gt;2</span> \n</p>\n<br />\n<br />\n<p style=\"text-align:left;margin-left:10pt;\" class=\"MsoNormal\" align=\"left\">\n	<span style=\"color: rgb(0, 64, 128); font-family: ;\" 微软雅黑?,?sans-serif?;font-size:7.5pt;?=\"\">4.</span> \n</p>\n<br />\n<br />\n<p style=\"text-align:left;margin-left:10pt;\" class=\"MsoNormal\" align=\"left\">\n	<span style=\"color: rgb(0, 64, 128); font-family: ;\" 微软雅黑?,?sans-serif?;font-size:7.5pt;?=\"\">INSERT INTO department </span> \n</p>\n<br />\n<br />\n<p style=\"text-align:left;margin-left:10pt;\" class=\"MsoNormal\" align=\"left\">\n	<span style=\"color: rgb(0, 64, 128); font-family: ;\" 微软雅黑?,?sans-serif?;font-size:7.5pt;?=\"\">VALUES (006,\'</span><span style=\"color: rgb(0, 64, 128); font-family: ;\" 微软雅黑?,?sans-serif?;font-size:7.5pt;?=\"\">物理</span><span style=\"color: rgb(0, 64, 128); font-family: ;\" 微软雅黑?,?sans-serif?;font-size:7.5pt;?=\"\">\')</span> \n</p>\n<br />\n<br />\n<p style=\"text-align:left;margin-left:10pt;\" class=\"MsoNormal\" align=\"left\">\n	<span style=\"color: rgb(0, 64, 128); font-family: ;\" 微软雅黑?,?sans-serif?;font-size:7.5pt;?=\"\">5.</span> \n</p>\n<br />\n<br />\n<p style=\"text-align:left;margin-left:10pt;\" class=\"MsoNormal\" align=\"left\">\n	<span style=\"color: rgb(0, 64, 128); font-family: ;\" 微软雅黑?,?sans-serif?;font-size:7.5pt;?=\"\">ALTER TABLE student </span> \n</p>\n<br />\n<br />\n<p style=\"text-align:left;margin-left:10pt;\" class=\"MsoNormal\" align=\"left\">\n	<span style=\"color: rgb(0, 64, 128); font-family: ;\" 微软雅黑?,?sans-serif?;font-size:7.5pt;?=\"\">ADD (email VARCHAR2(40))</span> \n</p>\n<br />\n<br />\n<p style=\"text-align:left;margin-left:10pt;\" class=\"MsoNormal\" align=\"left\">\n	<span style=\"color: rgb(0, 64, 128); font-family: ;\" 微软雅黑?,?sans-serif?;font-size:7.5pt;?=\"\">6.</span> \n</p>\n<br />\n<br />\n<p style=\"text-align:left;margin-left:10pt;\" class=\"MsoNormal\" align=\"left\">\n	<span style=\"color: rgb(0, 64, 128); font-family: ;\" 微软雅黑?,?sans-serif?;font-size:7.5pt;?=\"\">UPDATE student SET class_id=102 WHERE NAME = \'张三\'</span> \n</p>\n<br />\n<br />\n<p style=\"text-align:left;margin-left:10pt;\" class=\"MsoNormal\" align=\"left\">\n	<span style=\"color: rgb(0, 130, 66); font-family: ;\" 微软雅黑?,?sans-serif?;font-size:12pt;?=\"\"></span> \n</p>\n<br />\n<br />\n<p style=\"text-indent:21pt;\" class=\"MsoBodyText\">\n	<span></span> \n</p>\n<br />\n<br />', '0', null, null, null, null, null, null, null);
